https://en.wikipedia.org/wiki/Smoothness#Smooth_partitions_of_unity
https://en.wikipedia.org/wiki/Error_function#Approximation_with_elementary_functions

differentials

https://en.wikipedia.org/wiki/Gradient_descent
https://en.wikipedia.org/wiki/Newton%27s_method
https://en.wikipedia.org/wiki/Householder%27s_method
https://en.wikipedia.org/wiki/Automatic_differentiation
*/

// https://iquilezles.org/articles/distfunctions/
vec3 opLimitedRepetition( in vec3 p, in float s, in vec3 l){
    return p - s*clamp(round(p/s),-l,l);
}

float sdCone( vec3 p, vec2 c, float h )
{
  // c is the sin/cos of the angle, h is height
  // Alternatively pass q instead of (c,h),
  // which is the point at the base in 2D
  vec2 q = h*vec2(c.x/c.y,-1.0);

  vec2 w = vec2( length(p.xz), p.y );
  vec2 a = w - q*clamp( dot(w,q)/dot(q,q), 0.0, 1.0 );
  vec2 b = w - q*vec2( clamp( w.x/q.x, 0.0, 1.0 ), 1.0 );
  float k = sign( q.y );
  float d = min(dot( a, a ),dot(b, b));
  float s = max( k*(w.x*q.y-w.y*q.x),k*(w.y-q.y)  );
  return sqrt(d)*sign(s);
}

vec2 pRot(inout vec2 p, float a){
  p=cos(a)*p+sin(a)*vec2(p.y, -p.x);
  return p;
}

struct WindSource{
    vec3 pos;
    vec3 dir;
    float str;
};

WindSource ws0;
WindSource ws1;
WindSource ws2;
WindSource ws3;
vec3 gWindDir;
const vec3 gBASE_DIR = vec3(-1,0,0);

vec2 calcWindRot(vec3 p){
    vec3 dir = gBASE_DIR;
    dir = mix(ws0.dir, dir, smoothstep(0., ws0.str, distance(p, ws0.pos)));
    dir = mix(ws1.dir, dir, smoothstep(0., ws1.str, distance(p, ws1.pos)));
    dir = mix(ws2.dir, dir, smoothstep(0., ws2.str, distance(p, ws2.pos)));
    dir = mix(ws3.dir, dir, smoothstep(0., ws3.str, distance(p, ws3.pos)));
    gWindDir = dir;
    float z = atan(dir.y, dir.x);
    float y = atan(dir.z, dir.x);
    return vec2(z,y);
}

float map(vec3 p){
    p*=5.;
    vec3 ip = floor(p);
    vec2 ca = calcWindRot(ip);

    vec3 q = p;
    q = opLimitedRepetition(q+.5, 1., vec3(10));

    float a = .1*PI;
    float h = .75;
    pRot(q.yx, PI*.5);

    pRot(q.xy, ca.x);
    pRot(q.xz, ca.y);

    q.y-=h*.5;
    float d = sdCone(q, vec2(sin(a),cos(a)), h);
    d*=.5;

    #define DEF vec3(-1,0,0)
    float dif = dot(DEF, gWindDir);
    if(dif>.75)BLUE(d);
    if(dif>.5 && dif<.75)GREEN(d);
    if(dif<.5 && dif>-.5)YELLOW(d);
    if(dif<-.5 && dif>-.75)ORANGE(d);
    if(dif<-.75)RED(d);
    return d/5.;
}

void initWind(){
    ws0 = WindSource(vec3(cos(iTime*.75+5.),cos(iTime*.75),sin(iTime+5.))*15., vec3(sin(iTime*2.),0,cos(iTime*2.)), 20.);
    ws1 = WindSource(vec3(-sin(iTime*.25),0,-1)*5., vec3(0,0,1), (sin(iTime*.75+PI*.5)*.5+.5)*30.+10.);
    ws2 = WindSource(vec3(-1,cos(iTime*.5),-1)*10., vec3(cos(iTime*.5+PI),cos(iTime*.25+PI),-1), 40.);
    ws3 = WindSource(vec3(sin(iTime+15.),sin(iTime*.25+PI),cos(iTime+15.))*15., vec3(1,0,0), 20.);
}

void update(){
    initWind();
}

render();

