/*
  User Inputs

  References:
  "Special Shadertoy features" by Fabrice: https://shadertoyunofficial.wordpress.com/2016/07/20/special-shadertoy-features/
  "keyboard debugging" by mattz: https://www.shadertoy.com/view/4dGyDm
  "Input - Keyboard" by iq: https://www.shadertoy.com/view/lsXGzf
  "Input - Mouse" by iq: https://www.shadertoy.com/view/Mss3zH

  Shows how to use the mouse input (only left button supported):
       mouse.xy  = mouse position during last button down
   abs(mouse.zw) = mouse position during last button click
  sign(mouze.z)  = button is down
  sign(mouze.w)  = button is clicked

*/

const int K_PAD_0 = 96;
const int K_PAD_1 = 97;
const int K_PAD_2 = 98;
const int K_PAD_3 = 99;
const int K_PAD_4 = 100;
const int K_PAD_5 = 101;
const int K_PAD_6 = 102;
const int K_PAD_7 = 103;
const int K_PAD_8 = 104;
const int K_PAD_9 = 105;

const int K_NUM_0 = 48;
const int K_NUM_1 = 49;
const int K_NUM_2 = 50;
const int K_NUM_3 = 51;
const int K_NUM_4 = 52;
const int K_NUM_5 = 53;
const int K_NUM_6 = 54;
const int K_NUM_7 = 55;
const int K_NUM_8 = 56;
const int K_NUM_9 = 57;

const int K_W = 87;
const int K_A = 65;
const int K_S = 83;
const int K_D = 68;
const int K_E = 69;
const int K_Q = 81;
const int K_T = 84;
const int K_N = 78;
const int K_M = 77;
const int K_C = 67;
const int K_I = 73;
const int K_X = 88;
const int K_Y = 89;
const int K_Z = 90;

const int K_SHIFT = 16;
const int K_CTRL = 17;
const int K_ALT = 18;

#define ID(c) (distance(C, c)<1.)
#define getState(k)    ((texelFetch( iChannel0, ivec2(k, 0), 0 ).x)>.5)
#define getKeypress(k) ((texelFetch( iChannel0, ivec2(k, 1), 0 ).x)>.5)
#define getToggle(k)   ((texelFetch( iChannel0, ivec2(k, 2), 0 ).x)>.5)

void mainImage( out vec4 O, in vec2 C ){

    if(iFrame < 2 ){O = vec4(0);return;}

    vec4 last = texelFetch(iChannel1, ivec2(C), 0);

    if(ID(C_MOUSE)){O = (sign(iMouse.z)>.0) ? iMouse : last;return;}

    if(ID(C_CAMERA)){
        O = last;
        bool isFreeCamera = bool(last.y);
        if((getKeypress(K_NUM_0)||getKeypress(K_PAD_0)) && sign(iMouse.z)<=.0)O = vec4(0, 0, 0, 0);
        if(getState(K_NUM_1)||getState(K_PAD_1))O = vec4(1, true, 0, 0);
        if(getState(K_NUM_3)||getState(K_PAD_3))O = vec4(3, true, 0, 0);
        if(getState(K_NUM_7)||getState(K_PAD_7))O = vec4(7, true, 0, 0);
        return;}

    if(ID(C_CAM_SCALE)){
        if(getState(K_W))last-=.01;
        if(getState(K_S))last+=.01;
        O = last;
        return;}

    if(ID(C_TARGET_X)){
        if(getState(K_X) &&  getState(K_ALT))last+=.01;
        if(getState(K_X) && !getState(K_ALT))last-=.01;
        O = last;
        return;}

    if(ID(C_TARGET_Y)){
        if(getState(K_Y) &&  getState(K_ALT))last+=.01;
        if(getState(K_Y) && !getState(K_ALT))last-=.01;
        O = last;
        return;}

    if(ID(C_TARGET_Z)){
        if(getState(K_Z) &&  getState(K_ALT))last+=.01;
        if(getState(K_Z) && !getState(K_ALT))last-=.01;
        O = last;
        return;}
    if(ID(C_QUAD_VIEW))   {O = vec4(getToggle(K_Q));return;}
    if(ID(C_TURN_TABLE))  {O = vec4(getToggle(K_T));return;}
    if(ID(C_DEBUG_ISO))   {O = vec4(getToggle(K_I));return;}
    if(ID(C_DEBUG_COST))  {O = vec4(getToggle(K_C));return;}
    if(ID(C_DEBUG_MATCAP)){O = vec4(getToggle(K_M));return;}
    if(ID(C_DEBUG_NORMAL)){O = vec4(getToggle(K_N));return;}

    // blank pixel
    O = vec4(0,0,1,1);
}

