const float PI = 3.1415927;

void mainImage(out vec4 O, vec2 I) {
    vec2 R = iResolution.xy;
    vec2 p = (2.0 * I - R) / R.y * 3.0;
    float t = 1.0 - abs(1.0 - mod(iTime / 3.0, 2.0));
    float a = smoothstep(0.0, 1.0, smoothstep(0.0, 1.0, t)) * PI / 3.0;
    mat2 A = mat2(sqrt(3.0), 1.0, 0.0, 2.0);
    vec2 b = inverse(A) * p / cos(a - PI / 6.0);
    vec2 c = fract(b);
    vec2 d = floor(b) + step((clamp(3.0 * c, 1.0, 2.0) - c).yx, 2.0 * c);
    vec2 e = p - A * d * cos(a - PI / 6.0);
    a = mod(d.x, 2.0) == 1.0 ? -a : a;
    e = mat2(cos(a), sin(a), -sin(a), cos(a)) * e;
    vec2 f = abs(e);
    float g = max(f.y, sqrt(0.75) * f.x + 0.5 * f.y) - sqrt(0.75);
    float h = fract(sin(dot(d, vec2(12.9898, 78.233))) * 43758.5453);
    h = step(0.5, h) * PI / 3.0;
    float i = distance(e, vec2(cos(h), sin(h)));
    h += PI * 2.0 / 3.0;
    i = min(i, distance(e, vec2(cos(h), sin(h))));
    h += PI * 2.0 / 3.0;
    i = min(i, distance(e, vec2(cos(h), sin(h))));
    i = abs(i - 0.5);
    float j = dot(normalize(vec2(dFdx(i), dFdy(i))), normalize(vec2(1.0, 2.0)));

    if (g < 0.0) {
        O = vec4(0.8);

        if (g > -0.2) {
            O = vec4(0.6);
        }

        if (i < 0.2) {
            O = vec4(1.5 + 0.3 * j);
        } else {
            O *= pow(min((i - 0.2) / 0.2, 1.0), 0.07);
        }
    } else {
        O = vec4(0.3);
    }

    O *= vec4(0.114,0.576,0.667,1.0);
}

const float PI = 3.1415927;

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 R = iResolution.xy;
    vec2 p = (2.0 * fragCoord - R) / R.y * 4.0;
    float t = 1.0 - abs(1.0 - mod(iTime / 3.0, 2.0));
    float a = smoothstep(0.0, 1.0, smoothstep(0.0, 1.0, t)) * PI / 3.0;
    mat2 A = mat2(sqrt(3.0), 1.0, 0.0, 2.0);
    vec2 b = inverse(A) * p / cos(a - PI / 6.0);
    vec2 c = fract(b);
    vec2 d = floor(b) + step((clamp(3.0 * c, 1.0, 2.0) - c).yx, 2.0 * c);
    vec2 e = p - A * d * cos(a - PI / 6.0);
    a = mod(d.x + d.y, 2.0) == 1.0 ? -a : a; // use anything to determine spin direction!
    e = mat2(cos(a), sin(a), -sin(a), cos(a)) * e;
    vec2 uv = e + A * sqrt(0.75) * d;
    uv = (uv / 4.0 * R.y + R) / 2.0 / R;
    vec2 f = abs(e);
    float g = max(f.y, sqrt(0.75) * f.x + 0.5 * f.y) - sqrt(0.75);
