vec4 rayTriangleIntersection(vec3 ro, vec3 rd, mat3 triangle) {
    vec3 a = triangle[0];
    vec3 b = triangle[1];
    vec3 c = triangle[2];


    vec3 ab = b-a;
    vec3 ac = c-a;
    vec3 vn = cross(ab, ac);

    float vn_dot_rd = dot(vn, rd);
    float vn_dot_ps = dot(vn, a-ro);
    float t = vn_dot_ps / vn_dot_rd;

    vec3 vip = ro+rd*t;

    vec3 bc = c-b;
    vec3 ca = a-c;

    vec3 a_vip = vip-a;
    vec3 b_vip = vip-b;
    vec3 c_vip = vip-c;

    vec3 atest = cross(ab, a_vip);
    vec3 btest = cross(bc, b_vip);
    vec3 ctest = cross(ca, c_vip);

    return vec4(dot(atest,vn), dot(btest,vn), dot(ctest,vn), t);
}

int drawQuadrilateral(vec3 ro, vec3 rd, mat4x3 points) {
    int t;
    vec4 inter = rayTriangleIntersection(ro, rd, mat3(
        points[0],
        points[1],
        points[2]
    ));
    if (inter.x > 0. && inter.y > 0. && inter.z > 0.) t = 1;
    inter = rayTriangleIntersection(ro, rd, mat3(
        points[0],
        points[3],
        points[2]
    ));
    if (inter.x > 0. && inter.y > 0. && inter.z > 0.) t = 1;
    inter = rayTriangleIntersection(ro, rd, mat3(
        points[0],
        points[1],
        points[3]
    ));
    if (inter.x > 0. && inter.y > 0. && inter.z > 0.) t = 1;
    inter = rayTriangleIntersection(ro, rd, mat3(
        points[1],
        points[3],
        points[2]
    ));
    if (inter.x > 0. && inter.y > 0. && inter.z > 0.) t = 1;
    return t;
}

int drawRect(vec3 ro, vec3 rd, vec3 pos, vec2 size) {
    return drawQuadrilateral(ro, rd, mat4x3(
        vec3(pos),
        vec3(pos.xy, pos.z+size.y),
        vec3(pos.x, pos.yz+size),
        vec3(pos.x, pos.y+size.x, pos.z)
    ));
