// CC0: Quick hack for a twitch buddy
//  Watched a twitch stream that showed some concept art for the stream
//  I thought: I could shaderify that for fun!
//  This was a quick remix of existing shaders

#define TIME        iTime
#define RESOLUTION  iResolution
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

const float PI                  = acos(-1.)   ;
const float TAU                 = 2.0*PI      ;
const float PI_2                = 0.5*PI      ;
const float TOLERANCE           = 1.0E-4      ;
const float MAX_RAY_LENGTH      = 20.         ;
const float NORM_OFF            = 0.005       ;
const float MAX_RAY_MARCHES     = 40.0        ;

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

const float hoff      = 0.;
const vec3 skyCol     = HSV2RGB(vec3(hoff+0.57, 0.70, 0.25));
const vec3 sunDir     = normalize(vec3(0.0, 0.0, 1.0)) ;

float g_anim;

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/intersectors/
float rayPlane(vec3 ro, vec3 rd, vec4 p) {
  return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}

// License: Unknown, author: Matt Taylor (https://github.com/64), found: https://64.github.io/tonemapping/
vec3 aces_approx(vec3 v) {
  v = max(v, 0.0);
  v *= 0.6f;
  float a = 2.51f;
  float b = 0.03f;
  float c = 2.43f;
  float d = 0.59f;
  float e = 0.14f;
  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}

// License: MIT, author: Pascal Gilcher, found: https://www.shadertoy.com/view/flSXRV
float atan_approx(float y, float x) {
  float cosatan2 = x / (abs(x) + abs(y));
  float t = PI_2 - cosatan2 * PI_2;
  return y < 0.0 ? -t : t;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/distfunctions/
float box(vec2 p, vec2 b) {
  vec2 d = abs(p)-b;
  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/distfunctions/
float twistedBoxTorus(vec3 p, vec3 d) {
  vec2 q = vec2(length(p.xz) - d.x, p.y);
  float a = atan_approx(p.x, p.z);
  mat2 r = ROT(a+g_anim);
  return box(r*q, vec2(d.y))-d.z;
}

float df(vec3 p) {
  vec3 p0 = p.yzx;
  float d = twistedBoxTorus(p0, vec3(2.5, 0.5, 0.075));
  return d;
}

float rayMarch(vec3 ro, vec3 rd) {
  float t     = 0.0;
  vec2 nti = vec2(MAX_RAY_MARCHES,0);

  float i;
  for (i = 0.0; i < MAX_RAY_MARCHES; ++i) {
    if (t > MAX_RAY_LENGTH) break;
    float d = df(ro + rd*t);
    if (d<nti.x) nti = vec2(d,t);
    if (d < TOLERANCE) break;
    t += d;
  }

  if(i==MAX_RAY_MARCHES) t = nti.y;

  return t;
}


vec3 normal(vec3 pos) {
  vec2 eps = vec2(NORM_OFF, 0.0);
  return normalize(vec3(
      df(pos+eps.xyy)-df(pos-eps.xyy)
    , df(pos+eps.yxy)-df(pos-eps.yxy)
    , df(pos+eps.yyx)-df(pos-eps.yyx))
    );
}

vec3 render0(vec3 ro, vec3 rd) {
  vec3 col = vec3(0.0);

  col += 1E-2*(skyCol*skyCol)/(1.0001+dot(rd, sunDir));

  float tp0  = rayPlane(ro, rd, vec4(vec3(0.0, -1.0, 0.0), -5.0));
  float tp1  = rayPlane(ro, rd, vec4(vec3(0.0, -1.0, 0.0), 6.0));

  if (tp0 > 0.0) {
    col += 0.85*(skyCol)*exp(-0.5*(length((ro + tp0*rd).xz)));
  }

  if (tp1 > 0.0) {
    vec3 pos  = ro + tp1*rd;
    vec2 pp = pos.xz;
    float db = box(pp, vec2(5.0, 9.0))-3.0;

    col += vec3(4.0)*skyCol*rd.y*rd.y*smoothstep(0.25, 0.0, db);
    col += vec3(0.8)*skyCol*exp(-0.5*max(db, 0.0));
    col += 0.25*sqrt(skyCol)*max(-db, 0.0);
  }

  return col;
}

vec3 render1(vec3 ro, vec3 rd) {
  vec3 col = vec3(0.0);

  float te = rayMarch(ro, rd);
  if (te < MAX_RAY_LENGTH) {
    vec3 ep = ro+rd*te;
    vec3 en = normal(ep);
    vec3 er = reflect(rd, en);

    float fre = 1.0+dot(rd,en);
    fre *= fre;
    col += skyCol*0.125;
    col += mix(0.5, 2.0, fre)*render0(ep, er);
  } else {
    col += render0(ro,rd);
  }

  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  vec2 pp = p;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  g_anim = 0.125*TIME;
  const float fov = tan(TAU/6.);
  const vec3 ro = vec3(0.0, 0.1, 8.0);
  const vec3 la = vec3(0.0, 0.0, 0.0);
  const vec3 up = vec3(0.0, 1.0, 0.0);
  const vec3 ww = normalize(la - ro);
  const vec3 uu = normalize(cross(up, ww));
  const vec3 vv = (cross(ww,uu));

  vec3 rd = normalize(-p.x*uu + p.y*vv + fov*ww);

  vec3 col = render1(ro, rd);
  col -= 0.05*length(pp);
  col = aces_approx(col);
  col = sqrt(col);

  fragColor = vec4(col, 1.0);
}

