vec4 voronoi(vec2 uv, float scale, float seed){
    vec2 tv= fract(uv) * scale;
    vec2 ti = floor(tv);
    vec2 tf = fract(tv);
    float m_dist = 2.;
    vec2 m_point;
    for(int x = -1; x <= 1; x++){
        for(int y = -1; y<=1; y++){
            vec2 off = vec2(float(x),float(y));
            vec2 p = hash2(mod(ti + off,scale)+seed);
            vec2 diff = off + p - tf;
            float dist = length(diff);
            if(dist < m_dist) {
            m_dist = dist;
            m_point = p;
            }
        }
    }
    return vec4(fract(m_point.r*seed));
}

