void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = (fragCoord.xy / iResolution.xy) * 5.3;
    float seed = 1.0;

    float r = perlinNoise(p + iTime * 0.03);
    vec4 fgColor = FOREGROUND_COLORS[0];

    float noiseScale = 0.054 + 0.075 * mod(seed, 3.0);
    float numLayers = 2.0 + 2.0 * mod(seed, 5.0);
    seed *= numLayers;
    float v = 0.0;

    for (float i = 0.0; i < numLayers; i++) {
        v += step(0.7, noiseScale * perlinNoise(p + vec2(i + seed)) + r) / numLayers;
        noiseScale += 0.5;
    }

    vec4 finalColor = mix(FOREGROUND_COLORS[0] * 1.6, fgColor, v);

    seed = 8.0;
    r = perlinNoise(p + 20.0 + iTime * 0.03);
    fgColor = FOREGROUND_COLORS[0];

    noiseScale = 0.054 + 0.075 * mod(seed, 3.0);
    numLayers = 8.0 + 2.0 * mod(seed, 5.0);
    seed *= numLayers;
    v = 0.0;

    for (float i = 0.0; i < numLayers; i++) {
        v += step(0.3, noiseScale * perlinNoise(p + vec2(i + seed)) + r) / numLayers;
