float noise (in vec2 st, float seed) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    float a = random(i,seed);
    float b = random(i + vec2(1.0, 0.0),seed);
    float c = random(i + vec2(0.0, 1.0),seed);
    float d = random(i + vec2(1.0, 1.0),seed);

    vec2 u = f*f*(3.0-2.0*f);

    return mix(a, b, u.x) + 
            (c - a)* u.y * (1.0 - u.x) + 
            (d - b) * u.x * u.y;
}

