float mod289(float x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec4 mod289(vec4 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec4 perm(vec4 x) { return mod289(((x * 34.0) + 1.0) * x); }

float noise(vec3 p)
{
    vec3 a = floor(p);
    vec3 d = p - a;
    d = d * d * (3.0 - 2.0 * d);

    vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
    vec4 k1 = perm(b.xyxy);
    vec4 k2 = perm(k1.xyxy + b.zzww);

    vec4 c = k2 + a.zzzz;
    vec4 k3 = perm(c);
    vec4 k4 = perm(c + 1.0);

    vec4 o1 = fract(k3 * (1.0 / 41.0));
    vec4 o2 = fract(k4 * (1.0 / 41.0));

    vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
    vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);

    return o4.y * d.y + o4.x * (1.0 - d.y);
}

float noiseStack(vec3 pos,int octaves,float falloff){
	float n = noise(vec3(pos));
	float off = 1.0;
	if (octaves>1) {
		pos *= 2.0;
		off *= falloff;
		n = (1.0-off)*n + off*noise(vec3(pos));
	}
	if (octaves>2) {
		pos *= 2.0;
		off *= falloff;
		n = (1.0-off)*n + off*noise(vec3(pos));
	}
	if (octaves>3) {
		pos *= 2.0;
		off *= falloff;
		n = (1.0-off)*n + off*noise(vec3(pos));
	}
	return (1.0+n)/2.0;
}

vec2 noiseStackUV(vec3 pos,int octaves,float falloff,float diff){
	float displaceA = noiseStack(pos,octaves,falloff);
	float displaceB = noiseStack(pos+vec3(3984.293,423.21,5235.19),octaves,falloff);
	return vec2(displaceA,displaceB);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float min_res = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min_res;

    const float w = 0.05; // Line Width
    const vec3 c = vec3(0, 0.05, 1); // Line Color


    vec3 col = vec3(0);
    float audio = texelFetch(iChannel0, ivec2(0), 0).r;
    float y = sin((-iTime * 0.5 + uv.x) * PI) / (2.0 + uv.x * uv.x * 8.0) * audio;
    float vy = uv.y + 0.4;

    float d1 = smoothstep(w, 0.0, abs(vy - y));
    col += c * d1;
    col += d1 * d1 * d1 * d1 * 0.5;

    float d2 = smoothstep(w, 0.0, abs(vy + y));
    col += c * d2;
    col += d2 * d2 * d2 * d2 * 0.5;

    float d3 = smoothstep(w, 0.0, abs(vy - y * 0.5));
    col += c * d3 * 0.3;
    col += d3 * d3 * d3 * d3 * 0.2;

    float d4 = smoothstep(w, 0.0, abs(vy + y * 0.5));
    col += c * d4 * 0.3;
    col += d4 * d4 * d4 * d4 * 0.2;

    // Sparks
    const float up_flow = 8.0;
	float spark_grid_size = min_res / 20.0;
    float rt = iTime * 0.5;
	vec2 spark_uv = fragCoord - spark_grid_size * vec2(0, up_flow * rt);
	spark_uv -= spark_grid_size * noiseStackUV(0.01 * vec3(spark_uv, 30.0 * iTime), 1, 0.4, 0.1);
	if (mod(spark_uv.y / spark_grid_size, 2.0) < 1.0) spark_uv.x += 0.5 * spark_grid_size;
	vec2 spark_grid_idx = vec2(floor(spark_uv / spark_grid_size));
	float spark_random = rand(spark_grid_idx);
	float spark_life = min(10.0 * (1.0 - min((spark_grid_idx.y) / (24.0 - 20.0 * spark_random), 1.0)), 1.0) * (0.6 + 0.45 * audio);
	vec3 sparks = vec3(0);
	if (spark_life > 0.0) {
		float spark_size = spark_random * 0.3;
		float spark_radians = 999.0 * spark_random * 2.0 * PI + 2.0 * iTime;
		vec2 spark_circular = vec2(sin(spark_radians), cos(spark_radians));
		vec2 spark_off = (0.5 - spark_size) * spark_grid_size * spark_circular;
		vec2 spark_modulus = mod(spark_uv + spark_off, spark_grid_size) - 0.5 * vec2(spark_grid_size);
		float spark_length = length(spark_modulus);
		float sparks_gray = max(0.0, 1.0 - spark_length / (spark_size * spark_grid_size));
		sparks = spark_life * sparks_gray * c + pow(spark_life * sparks_gray, 4.0);
	}
    sparks *= smoothstep(-0.4, -0.0, uv.y);
    sparks *= smoothstep(1.0, 0.0, uv.y);
    col += sparks;

    col *= 1.0 - abs(fragCoord.x / iResolution.x * 2.0 - 1.0);

    fragColor = vec4(col,1.0);
}

const float PI = 3.14159265358;

vec4 smin(vec4 a, vec4 b, float k) {
    float h = clamp(0.5 + 0.5 * (a.w - b.w) / k, 0.0, 1.0);
    return mix(a, b, h) - k * h * (1.0 - h);
}

float luma(vec3 color) {
  return dot(color, vec3(0.299, 0.587, 0.114));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float min_res = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min_res * 1.5;
    float t = iTime;
    const float w = 0.01; // Line Width
    const float f = 1.0; // Frequency
    const float b = 60.0; // Bands
    float amp = 0.8 * texelFetch(iChannel0, ivec2(0), 0).r; // Amplitude

    float xd = abs(uv.x);
    float falloff = (1.0 - exp(-xd * xd) + uv.x * uv.x * 0.05);
    vec4 d = vec4(vec3(0), 999999.0);
    float off = t * 2.0;
    float fm = (1.0 + 0.3 * sin(t));
    float x = uv.x * PI * f * fm - off;
    vec4 y1 = vec4(vec3(1.0, 0.0, 0.0), sin(x));
    vec4 y2 = vec4(vec3(0.0, 0.7, 1.0), sin(x + 1.0) * 0.5);
    vec4 y3 = vec4(vec3(1.0, 0.0, 1.0), sin(x + 1.8) * 1.1);
    vec4 y4 = vec4(vec3(0.0, 1.0, 1.0), sin(x + 3.0) * 0.7);
    float am = amp / (1.0 + xd * xd * 4.0);
    for (float i = 0.0; i <= 1.001; i += 1.0 / b) {
        vec4 yy1 = mix(y1, y2, i);
        vec4 yy2 = mix(y3, y4, i);
        vec4 y = mix(yy1, yy2, i);
        y.w = abs(uv.y - y.w * am) - w + falloff * 0.05;
        y.rgb *= y.rgb;
        d = smin(y, d, 0.05);
    }
    float a = 0.01;
    float c = smoothstep(a, -a, d.w);
    vec3 col = d.rgb * sqrt(c) * 0.5;
    col += pow(luma(col), 0.3) * 1.0;

    fragColor = vec4(col, 1.0);
}

float FPS = 10.f;
ivec2 AtlasSize = ivec2(6, 1);
ivec2 FrameSize = ivec2(40.f, 32.f);

void getNyan( inout vec4 fragColor, in vec2 uv ) {
    int frame = int(floor(iTime*FPS));
    int frameNum = frame % (AtlasSize.x * AtlasSize.y);
    ivec2 currentFramePosition = ivec2(frameNum % AtlasSize.x, frameNum / AtlasSize.x);

    vec4 color = texture(iChannel1, uv/vec2(AtlasSize) + vec2(currentFramePosition*FrameSize)/iChannelResolution[1].xy);
    if (color.w > 0.9) fragColor = color*color*1.55;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 v3 = texelFetch( iChannel0, ivec2(int(fragCoord.x),int(fragCoord.y)), 0 ).xyz;

    vec2 uv = (fragCoord - (0.5 * iResolution.xy)) / iResolution.y;
    vec3 white = abs(uv.y) < 0.43 && abs(uv.y) > 0.427 && abs(uv.x)<0.85 ? vec3(0.8,1,1) : vec3(0.);
    fragColor = vec4( v3 + white, 1.0 );

    ivec2 r = ivec2(iResolution.xy);
    ivec4 npos = ivec4(texelFetch(iChannel2, ivec2(2,0), 0 ));

    vec2 sp = getStartPos(npos.y, iResolution.xy, iTime);
    if (int(texelFetch(iChannel2, ivec2(1,0), 0 ).x) == 1) { sp = vec2(float(int(npos.x)%r.x), iResolution.y - float(int(npos.x)/r.x)); }

    vec2 pos = (sp/iResolution.y) - 0.5;
    pos.x += (1. - iResolution.x/iResolution.y) * 0.5;

    vec2 siz = vec2(0.077,0.067);
    if (abs(uv.x-pos.x) < siz.x && abs(uv.y-pos.y)< siz.y) {
        vec2 uv2 = ((uv-pos)/siz*0.5 + 0.5);
        getNyan(fragColor, uv2);
    }
}

