                for (int i = 0; i < 5000; i++) { // search for lit pixel
                    v+=1*tv;
                    vec2 p=vec2(v%r.x,r.y-v/r.x);

                    if (texelFetch(iChannel1,ivec2(p),0).x > 0.1) {
                        break;
                    }
                }
                val[j] = v;
            }
        }
        fragColor = vec4(val);
        return;
    }

    if (ivec2(1,0) == ivec2(fragCoord)) {
        ivec4 val = ivec4(texelFetch(iChannel0, ivec2(0,0), 0 ));
        int tv =  int(texelFetch(iChannel0, ivec2(1,0), 0 ).x);
        if (iFrame < 2) {
