            if (val.x < 0) tv=0;
        }
        fragColor = vec4(tv);
        return;
    }
    if (ivec2(2,0) == ivec2(fragCoord)) {
        fragColor = texelFetch(iChannel0, ivec2(0,0), 0 );
        return;
    }

    vec2 sp=vec2(0), ep=vec2(0);
    int dotFound = -1;
    for (int i=0; i<DOTS_PER_FRAME; i++) {
        if ((pxi+i*(NOF_STEPS+1)) % NOF_DOTS == iFrame % NOF_DOTS)
            dotFound = i;
    }

    if (dotFound >= 0) { // initialize new dot
        int i = int(texelFetch(iChannel0, ivec2(0,0), 0 )[dotFound]);
        vec2 p=vec2(i%r.x,r.y-i/r.x);

        if (texelFetch(iChannel1,ivec2(p),0).x > 0.1) {
            sp = getStartPos(i, iResolution.xy, iTime);
            ep = p;
            if (int(texelFetch(iChannel0, ivec2(1,0), 0 ).x) == 1) { ep=sp; sp=p; }
        }
        fragColor = vec4(sp, ep);
    }
    else { // update active dot
        vec4 cs = texelFetch(iChannel0, ivec2(fragCoord), 0 );
        int count = (int(cs.w) >> 12) + 1;
        int ai = int(cs.w) & 4095;
        cs.w = float((count<<12) | ai);
        vec2 ep = vec2(cs.z, ai);

        if (count >= NOF_STEPS) {
            cs.x=ep.x; cs.y=ep.y; cs.w=float((NOF_STEPS<<12) | ai);
        } else {
            vec2 cp = cs.xy;
            vec2 ap = (ep-cp)/(float(NOF_STEPS)-float(count));
            cs.x += ap.x;
            cs.y += ap.y;
        }
        fragColor = cs;
    }
}

