const int NOF_STEPS = 120;

const int DOTS_PER_FRAME = 4; // 1-4, higher value writes faster

const int NOF_DOTS = (NOF_STEPS + 1) * DOTS_PER_FRAME;

const int NOF_REGIONS = 9; // 2-9
const int BPC = NOF_REGIONS * NOF_REGIONS /4 + 1;

#define LARGE_DOTS

const int KEY_SPACE = 32;
const int KEY_ENTER = 13;
const int KEY_D     = 68;

#define readKey(key) (texelFetch( iChannel3, ivec2(key, 0), 0).x > .5)
#define readKeyToggle(key) (texelFetch( iChannel3, ivec2(key, 2), 0).x > .5)

const vec4 rainbow[6] = vec4[]( vec4(1,0,0,0), vec4(1,0.66,0,0), vec4(1,1,0,0), vec4(0,1,0,0), vec4(0,0.6,1,0), vec4(1,0.3,1,0) );

vec2 getStartPos(int i, vec2 resolution, float time) {
    vec2 sp;
    float progr = float(i / int(resolution.x)) / resolution.y;
    if (progr > 0.7)
        sp = vec2(resolution.x*0.5+sin(time*8.)*(resolution.x*0.25), resolution.y*0.82);
    else if (progr > 0.4)
        sp = vec2(0.55*resolution.x+sin(time*3.)*(resolution.x*0.1), 0.44*resolution.y+cos(time*3.)*(resolution.y*0.16));
    else
        sp = vec2(resolution.x*0.5+sin(time*6.)*(resolution.x*0.19), resolution.y*0.2);
    return sp;
}

