    if (ended > 0) { fragColor = vec4(texelFetch(iChannel2, ivec2(fragCoord), 0 ).xyz * rcol.xyz, ended == 1 ? org.w : END_VAL); return; }
    vec4 cols[2] = vec4[]( vec4(0,0,1,0),vec4(0,1,0,0) );
    fragColor = min(vec4(1.),org);
}

