    float rotY = iMouse.x / iResolution.x * 8.;
    rotY += iTime * 0.1;

    vec3 cam = vec3(0, 0, 0.0);
    mat3 rotationMatrix = mat3(
        cos(rotY), 0, sin(rotY),
        0, 1, 0,
        -sin(rotY), 0, cos(rotY)
    ) * mat3(
        1, 0, 0,
        0, cos(rotX), -sin(rotX),
        0, sin(rotX), cos(rotX)
    );

    cam = rotationMatrix * cam;
    dir = normalize(rotationMatrix * dir);

    col = march(cam, dir);

    fragColor = vec4(col, 1.0); // Output to screen
}

