void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec2 uv = fragCoord.xy/iResolution.xy;
	uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

	uv.x += .09;

	float border = 1./iResolution.y;

	float zoom = .08;

	uv*=zoom;
	border*=zoom;

	vec4 dis_sq = vec4(1000);

	vec3 color = col3;

	if(all(lessThan(uv,vec2(0.0363569,0.0273438)+border)) && all(greaterThan(uv,vec2(-0.0363569,-0.0273438)-border))){
		dis_sq=dmin(dis_sq,path0_dis_sq(uv));
		dis_sq=dmin(dis_sq,path1_dis_sq(uv));
		dis_sq=dmin(dis_sq,path2_dis_sq(uv));
