		dis_sq=dmin(dis_sq,path4_dis_sq(uv));
		dis_sq=dmin(dis_sq,path5_dis_sq(uv));
		dis_sq=dmin(dis_sq,path6_dis_sq(uv));
		dis_sq=dmin(dis_sq,path7_dis_sq(uv));
		dis_sq=dmin(dis_sq,path8_dis_sq(uv));
		dis_sq=dmin(dis_sq,path9_dis_sq(uv));
		dis_sq=dmin(dis_sq,path10_dis_sq(uv));
		dis_sq=dmin(dis_sq,path11_dis_sq(uv));
		dis_sq=dmin(dis_sq,path12_dis_sq(uv));
		dis_sq=dmin(dis_sq,path13_dis_sq(uv));
		dis_sq=dmin(dis_sq,path14_dis_sq(uv));
		dis_sq=dmin(dis_sq,path15_dis_sq(uv));
		dis_sq=dmin(dis_sq,path16_dis_sq(uv));
		dis_sq=dmin(dis_sq,path17_dis_sq(uv));
		dis_sq=dmin(dis_sq,path18_dis_sq(uv));
		dis_sq=dmin(dis_sq,path19_dis_sq(uv));
		dis_sq=dmin(dis_sq,path20_dis_sq(uv));
		dis_sq=dmin(dis_sq,path21_dis_sq(uv));
		dis_sq=dmin(dis_sq,path22_dis_sq(uv));
		dis_sq=dmin(dis_sq,path23_dis_sq(uv));
		dis_sq=dmin(dis_sq,path24_dis_sq(uv));
		dis_sq=dmin(dis_sq,path25_dis_sq(uv));
		dis_sq=dmin(dis_sq,path26_dis_sq(uv));
		dis_sq=dmin(dis_sq,path27_dis_sq(uv));
		dis_sq=dmin(dis_sq,path28_dis_sq(uv));
		dis_sq=dmin(dis_sq,path29_dis_sq(uv));
		dis_sq=dmin(dis_sq,path30_dis_sq(uv));
		dis_sq=dmin(dis_sq,path31_dis_sq(uv));
		dis_sq=dmin(dis_sq,path32_dis_sq(uv));
		dis_sq=dmin(dis_sq,path33_dis_sq(uv));
		dis_sq=dmin(dis_sq,path34_dis_sq(uv));
		dis_sq=dmin(dis_sq,path35_dis_sq(uv));
		dis_sq=dmin(dis_sq,path36_dis_sq(uv));
		dis_sq=dmin(dis_sq,path37_dis_sq(uv));
		dis_sq=dmin(dis_sq,path38_dis_sq(uv));
		dis_sq=dmin(dis_sq,path39_dis_sq(uv));
		dis_sq=dmin(dis_sq,path40_dis_sq(uv));
		dis_sq=dmin(dis_sq,path41_dis_sq(uv));
		dis_sq=dmin(dis_sq,path42_dis_sq(uv));
		dis_sq=dmin(dis_sq,path43_dis_sq(uv));
		dis_sq=dmin(dis_sq,path44_dis_sq(uv));
		dis_sq=dmin(dis_sq,path45_dis_sq(uv));
		dis_sq=dmin(dis_sq,path46_dis_sq(uv));
		dis_sq=dmin(dis_sq,path47_dis_sq(uv));
	}

	float dis = abs(mod(dot(uv,normalize(vec2(1))),.02)-.01)-.005;
	color = mix(col1*.3+.7*color,color,smoothstep(-border,border,dis));

	dis=sign(dis_sq.w)*sqrt(abs(dis_sq.w));
	vec3 cur_col = dis_sq.rgb;

	color = mix(cur_col,color,smoothstep(-border,border,dis));

	color = mix(vec3(0),color,smoothstep(-border,border,abs(dis)-line_width));

	fragColor = vec4(color,1);
