// Blob vs Blob
// by Ruud Helderman, October 2023 - MIT License

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // normalize screen coordinates
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / (iResolution.x + iResolution.y);

    // scale; center bulge
    uv *= 24.0 - 6.0 / (0.4 + dot(uv, uv));    // alt: uv *= 12.0 * inversesqrt(1.0 - dot(uv, uv));

    // horizontal drift
    uv.x += iTime;

    // squirming shapes
    uv = sin(uv + sin(1.4 * uv.yx + 0.6 * sin(1.9 * uv + iTime)));

    // color cycling
    vec3 color = sin(vec3(0, 2.1, 4.2) - 0.2 * iTime);

    // shades
    color *= 1.0 - dot(uv, uv);    // alt: color *= 1.0 - length(uv);

    // depth for cineshader.com
    float alpha = 0.25 * length(color);

    // output
    fragColor = vec4(color, alpha);    // alt: fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(cos(uv.y + iTime + uv.x * 1.0), cos(0.2 * iTime), sin(uv.x + iTime + uv.y));
    // Output to screen
    fragColor = vec4(col,1.0);
}

// Fork of "Hinged Squares" by fad. https://shadertoy.com/view/DddBzM
// + inspiration from https://twitter.com/KeishiroUeki/status/1715924571180990920

// -6 by zlonka

#define S(v) smoothstep( 12./R.y, 0., v )   

void mainImage(out vec4 O, vec2 I) {
    float P = 3.14, g=.5,
          a = smoothstep(0.,1., smoothstep(g,0., abs( fract(iTime/4.) - g ) )) * P * g,
          c = cos(a),
          s = sin(a),
          b = c + s;
    vec2  R = iResolution.xy,
          p = 8.* ( I+I - R ) / R.y / b,
          d = mat2(c, s, -s, c)  * asin(cos(p)) * b / P,
          D = abs(d),
    q = cos(1e3*sin(dot(ceil(p/P),R-57.))) > .0 ? vec2(-d.y,d) : d;    // random PI/2 turn
    O =  ( g + S( abs( length( q - g*sign(q.x+q.y) ) -g ) - .05 ) )    // truchet
        * S( max(D.xxxx, D.y) - g );                                   // tile
}





