vec2 T(vec2 v) {
    // https://mathworld.wolfram.com/ComplexExponentiation.html
    float d = dot(v, v),
          a = atan(v.y, v.r),
          t = dot(v, vec2(a, log(d) / 2.));

    return exp(-a * v.y) * pow(d, v.r / 2.) * vec2(cos(t), sin(t));
}


void mainImage(out vec4 o, vec2 i)
{
    // https://en.wikipedia.org/wiki/Orbit_trap
    vec2 z, t, w, 
         R = iResolution.xy,
         M = 2. * iMouse.xy - R;
    z = t = i = 2. * (i + i - R) / R.x;
    float j, f, r = 9.;

    // https://en.wikipedia.org/wiki/Julia_set
    for (M = 2. * M / R.x; j++ < 48.; f < r ? r = f, t = z : t)
        f = dot(z, z = 
            mix(i, M, max(0., sign(iMouse.z)))
            + (w = T(z))
        );

    // nonlinearly proportional to distance
    float d = 3. * sqrt(r) + exp(-length(z)),
          // grid
          g = smoothstep(0., 4., length(fract(w))),
          // hue angle
          // https://en.wikipedia.org/wiki/Argument_(complex_analysis)
          h = atan(t.y, t.x) + iTime / 4.;

    // normal map
    vec3 n = normalize(cross(dFdx(vec3(i, d)), dFdy(vec3(i, d)))),
         // light ray
         e = normalize(vec3(1, -1, -1)),
         // https://en.wikipedia.org/wiki/Color_wheel
         v = vec3(h - 2.1, h, h + 2.1);
    // https://en.wikipedia.org/wiki/Phong_reflection_model
    float l = (max(0., dot(e, n)),
            + max(0., dot(reflect(e, n), vec3(0, 0, -1)))
    ) / 2.;

    // https://en.wikipedia.org/wiki/Versine#Definitions
    o = vec4(.5 + .5 * cos(v), 1.);

    o = smoothstep(.0, .6, l * o + g);
}


