void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float frameCount = float(iFrame);

    // Use modulo on iTime to determine which buffer to display
    if(mod(frameCount, 2.0) == 1.0)
    {
        fragColor = texture(iChannel0, fragCoord/iResolution.xy); // Display Buf A
    }
    else
    {
        fragColor = texture(iChannel1, fragCoord/iResolution.xy); // Display Buf B
    }
}

