void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame == 0) // only seed the initial state on the first frame
    {
        float isTree = step(0.7, rand(fragCoord)); // roughly 70% chance to be a tree
        fragColor = vec4(0.0, isTree, 0.0, 1.0); // Trees are green
        return;
    }

    vec2 uv = fragCoord/iResolution.xy;

    // Sample the current state of the pixel
    vec3 currentState = texture(iChannel0, uv).rgb;

    // Get the states of Von Neumann neighbors (Up, Down, Left, Right)
    vec3 up = texture(iChannel0, mod(uv + vec2(0.0, 1.0/iResolution.y), 1.0)).rgb;
    vec3 down = texture(iChannel0, mod(uv - vec2(0.0, 1.0/iResolution.y), 1.0)).rgb;
    vec3 left = texture(iChannel0, mod(uv - vec2(1.0/iResolution.x, 0.0), 1.0)).rgb;
    vec3 right = texture(iChannel0, mod(uv + vec2(1.0/iResolution.x, 0.0), 1.0)).rgb;

    // Parameters
    float p = 0.01; // probability for a tree to catch fire if no neighbor is burning
    float g = 0.001; // Base probability for an empty cell to grow a tree

    // Check for fire in the neighborhood
    bool fireNearby = up.r > 0.5 || down.r > 0.5 || left.r > 0.5 || right.r > 0.5;

    vec3 nextState = currentState;

    // Fire logic
    if(currentState.r == 1.0) // Currently on fire
    {
        nextState = vec3(0.0, 0.0, 0.0); // Becomes empty
    }

    if(currentState.g == 1.0) // Currently a tree
    {
        if(fireNearby)
        {
            nextState = vec3(1.0, 0.0, 0.0); // Set on fire
        }
    }

    if(currentState.g == 0.0 && currentState.r == 0.0)
    {
        if(rand(fragCoord.xy + vec2(iTime) * (iResolution.y + iResolution.x)) < p)
        {
            nextState = vec3(1.0, 0.0, 0.0);
        }
        else if(rand(fragCoord.xy + vec2(iTime) * (iResolution.x + iResolution.z)) < g) // Empty space grows a tree with chance g at intervals
        {
            nextState = vec3(0.0, 1.0, 0.0); // Grow tree
        }
    }


    fragColor = vec4(nextState, 1.0);
}

