{
    // Fetch the existing pixel color from buffer C
    vec4 existingColor = texelFetch(iChannel1 , ivec2(fragCoord), 0);
    vec4 overwriteColor = texelFetch(iChannel2, ivec2(fragCoord), 0);

    // Get the position of the particle at this pixel in buffer A
    vec4 particle = getParticle(fragCoord);

    // Check if the particle is active 
    if (particle.w > 1.0)
    {
        // Calculate the distance between the current pixel and the particle
        float distance = length(fragCoord - particle.xy);

        // Set a trail distance threshold

        float agentTrailDistance = particle.w*3.;

        float trailDistanceThreshold = 3.;

        // Set a trail opacity
        float trailOpacity = 0.04;

        // Only update the pixel if it's within the trail distance threshold
        if (distance < agentTrailDistance)
        {
            if (reset(iChannel1))
            fragFood = mix(overwriteColor, vec4(0), trailOpacity);
             else{
            fragFood = mix(overwriteColor, vec4(0), trailOpacity);
            }
        }
        else
        {
            // If the pixel is outside the trail distance, retain the existing color
            fragFood = overwriteColor;
        }
    }
    else
    {
        // If no particle, retain the existing pixel color
        fragFood = existingColor;
    }

}

