/*
This is more of an experiment. This compares Mip blur to dither blur, 
both of which are extremely cheap with a single sample. 

The right side uses dithering to offset the UV coordinates of the textures. 
If you use Temporal Anti-Aliasing (TAA), it will automatically denoise your 
blur, giving you a cheap blur. Curiously, the hash functions look actually 
better for once compared to the dither functions from Valve or COD. This 
could be useful if you can't use Mips.

The left side is mip blur where we simply use the stored mip level to blur 
the textures. I would suggest using this one if you need a very cheap blur.

This demo includes mouse controls to adjust the noise strength and border.
Also, you can adjust the quasi motion blur sample count in the parameters 
below. 

I am not the first to try these, here is a noise blur from eliasku:
https://www.shadertoy.com/view/7lGBzd

And here is a Mip blur from the one and only IQ:
https://www.shadertoy.com/view/4slGWn
*/

// The number of previous samples looked at to blur result, with a similar 
// effect to Temporal Anti-Aliasing (TAA) which you should use instead.
#define TEMPORALBLUR 8

// valve edition (slightly modified to be a true 12 dither)
// http://alex.vlachos.com/graphics/Alex_Vlachos_Advanced_VR_Rendering_GDC2015.pdf
// input in pixels (ie not normalized uv)
vec2 ScreenSpaceDither22(vec2 vScreenPos, float time) {
    float vDither = dot( vec2( 171.0, 231.0 ), vScreenPos.xy + time); 
    return fract( vDither / vec2(103.0, 71.0));
}

// Function that uses dithering to blur an image
vec3 ditherblur(vec2 uv, vec2 fragCoord, float time, float blurStrength) {

    // Sample your noise with animation, and scale to vector
    vec2 dither = ScreenSpaceDither22(fragCoord,time) -0.5;

    // Calculate noisy UVs
    vec2 noisyUV = fract(uv - dither * blurStrength);

    // Calulate the buffer size
    vec2 texSize = vec2(textureSize(iChannel0, 0));
    ivec2 texelCoord = ivec2(noisyUV * texSize);

    // sample texels with random offsets   
    vec3 color = texelFetch(iChannel0, texelCoord, 0).xyz;

    return color;
}

// Blurs an image using the mip level, blurstrength is in UV scale
vec3 mipBlur(vec2 uv, float blurStrength) {
    // Sample texture resolution
    vec2 texSize = vec2(textureSize(iChannel0, 0));

    // Find out the max mip level
    float maxMipLevel = log2(max(texSize.x, texSize.y));

    // Calculate pixel range of the blur
    float blurPixelRange = max(texSize.x, texSize.y) * blurStrength;

    // Calculate mip level based on blur pixel range
    float mipLevel = log2(blurPixelRange);

    // Sample texture
    vec3 color = textureLod(iChannel0, uv, mipLevel).xyz;
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Enable mouse controls when clicked, otherwise animate
    float border;
    float blurStrength;
    if (iMouse.x > 0.0) {
        // Mouse controls
        vec2 mouse = iMouse.xy / iResolution.xy;
        border = mouse.x;
        blurStrength = mix(0.0, 0.1, pow(mouse.y,2.));
    } else {
        // Animation
        border = sin(iTime * 0.5) *0.5+0.5;
        blurStrength = mix(0.0, 0.1, pow(cos(iTime * 0.3) *0.5+0.5,2.));
    }

    // Correct blur for non-square aspect ratio
    blurStrength *= iResolution.x/iResolution.y;   

    // Alternate between mip blur and dithered blur
    vec3 color;
    if (uv.x > border) {
        // Right dithered
        #if TEMPORALBLUR <= 1
            // We just take a single sample from the blurred function.
            color += ditherblur(uv, fragCoord, iTime, blurStrength);
        #else
            // We take multiple samples from the ditheredSample function and average them.
            // This is normally done automatically by your TAA or motion blur
            for(int i = 0; i < TEMPORALBLUR; i++) {
                color += ditherblur(uv, fragCoord, iTime - float(i), blurStrength);
            }
            color /= float(TEMPORALBLUR);

        #endif
    } else {
        // Left mip blur
        color += mipBlur(uv, blurStrength).xyz;
    }

    // Add a dividing line
    color -= step(abs(uv.x - border), 0.002);

    // Output to screen
    fragColor = vec4(color,1.0);
}

#define tileScale 4.0
#define localOrigin vec2(0.5)
#define worldOrigin vec2(0.0)

#define random(p)        fract(sin(dot(p, vec2(123.456, 456.789))) * 789123.123)
#define pingpong(x)      fract( mod(x,2.0) < 1.0 ? x : -(x) )
#define dot2(v)          return dot(v,v);

// https://easings.net/
#define easeOutExpo(x) ( x == 1.0 ? 1.0 : 1.0 - exp2(-(x)/0.1) )
float easeOutBack(float x) {
    float c1 = 1.70158 * 5.0;
    float c3 = c1 + 1.1;
    float c4 = abs(x - 1.0);
    float c5 = x - 1.0;

    return 1.0 + c3 * c5*c5*c5 + c1 * c4*c4;
}

float easeInOutCirc(float x) {
    float c1 = 2.0 * x;
    float c2 = abs(-2.0 * x + 2.0);

    return x < 0.5
        ? (1.0 - sqrt(abs(1.0 - c1*c1))) / 2.0
        : (sqrt(abs(1.0 - c2*c2)) + 1.0) / 2.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x = uv.x * iResolution.x / iResolution.y;
	uv = tileScale*(2.0*fragCoord-iResolution.xy)/iResolution.y;


    // reference for other projects
    vec2 scaledUv = uv * tileScale;
    vec2 tileUv = fract(scaledUv);
    vec2 tileIndexXY = floor(scaledUv);
    float tileIdex = tileIndexXY.x + tileIndexXY.y*tileScale;
    float distToTile = distance(worldOrigin, tileIndexXY) / tileScale;

    float checkPattern = (mod(tileIndexXY.x, 2.0) == 0.0 ? 1.0 : 0.0) == (mod(tileIndexXY.y, 2.0) <= 0.0 ? 1.0 : 0.0) ? 1.0 : 0.0;

    float beat = 1.35;
    float animationSpeed_circleScale = (floor(iTime*beat) + easeOutBack(fract(iTime*beat))) * 0.8;

    float randomViaTileXYandDistToTile = mix(random(tileIndexXY), distToTile, 0.3);
    float circleRadius = 0.5 * easeOutExpo(pingpong(animationSpeed_circleScale + (randomViaTileXYandDistToTile*2.0-1.0)));
    circleRadius = pow(circleRadius, 1.1);

    float circle = mix(0.0, 1.0, smoothstep(tileScale*7.5/iResolution.y, 0.0, distance(tileUv, localOrigin) - circleRadius));

    vec4 normal = vec4(vec3(circle), 1.0);
    vec4 check = vec4( mix( checkPattern, 1.0-checkPattern, smoothstep(tileScale*7.5/iResolution.y, 0.0, distance(tileUv, localOrigin)-circleRadius) ) );

	vec3 col = mix(normal, check, easeInOutCirc(pingpong(iTime*beat*2.0))).xyz;
    fragColor = vec4(col, 0.2+length(col)*0.05);
}

