}

vec4 axisAngleToQuaternion(vec3 aa)
{
    float angle = length(aa);
    vec3 axis = normalize(aa);
    return vec4(axis * sin(angle * 0.5), cos(angle * 0.5));
}

vec3 qrot(vec3 x, vec4 q)
{
