/*

This shader demonstrates the white furnace test.

Written to illustrate the article:
https://lousodrome.net/blog/light/2023/10/21/the-white-furnace-test/

It's not just an expensive way to render an empty screen,
but a rendering test showing that the Monte Carlo
integration is done correctly.

When the scene appears empty, it is in fact rendering a
single white sphere in a uniformly lit environment.
Additional smaller spheres are rendered to darken or
lighten the edges of the white sphere and thus make it
"appear". Similarly, it becomes visible when using a non
uniform environment.

The material used is a Lambert BRDF, which is perfectly
energy conserving, to illustrate well the effect of the
white furnace.

License: CC BY 4.0

--
Zavie

*/

// Set to 1 for an incorrect integration, and see how it
// that error is revealed by the test.
#define SIMULATE_INCORRECT_INTEGRATION 0

#define SAMPLES_PER_FRAME 2
#define MAX_DEPTH 10

// Set to 1 to accumulate samples over multiple frames.
#define ENABLE_ACCUMULATION 1
#define MAX_ACCUMULATION_SAMPLES 40

// Set to 1 to add two coloured spheres on the sides,
// thus revealing the white sphere in the center.
#define SHOW_COLORED_SPHERES 1

// Set to 1 so the environement is not uniform,
// thus revealing the white sphere in the center.
#define SHOW_NON_UNIFORM_ENVIRONMENT 1

// Set SAMPLING to either UNIFORM_SAMPLING or COSINE_SAMPLING.
#define UNIFORM_SAMPLING 1
#define COSINE_SAMPLING 2
#define SAMPLING COSINE_SAMPLING

#define PI acos(-1.)

// ---8<----------------------------------------------------------------------
// Material

struct Material
{
    vec3 e;		// luminance
    vec3 c;		// diffuse color
    float f0;	// specular color (monochrome)
};

// ---8<----------------------------------------------------------------------
// Geometry

struct Ray
{
    vec3 o;		// origin
    vec3 d;		// direction
};

struct Hit
{
    float t;	// solution to p=o+t*d
    vec3 n;		// normal
    Material m;	// material
};
const Hit noHit = Hit(1e10, vec3(0.), Material(vec3(0.), vec3(0.), 0.));

struct Sphere
{
	float r;	// radius
    vec3 p;		// center position
    Material m;	// material
};

Hit intersectSphere(Sphere s, Ray r)
{
	vec3 op = s.p - r.o;
    float b = dot(op, r.d);
    float det = b * b - dot(op, op) + s.r * s.r;
    if (det < 0.) return noHit;

    det = sqrt(det);
    float t = b - det;
    if (t < 0.) t = b + det;
    if (t < 0.) return noHit;

    return Hit(t, (r.o + t*r.d - s.p) / s.r, s.m);
}

bool compare(inout Hit a, Hit b)
{
    if (b.m.f0 >= 0. && b.t < a.t)
    {
        a = b;
        return true;
    }
    return false;
}

Hit intersectScene(Ray r)
{
    Sphere snowBall = Sphere(1., vec3(0.0), Material(vec3(0.0), vec3(1.0), 0.04));

    Hit hit = noHit;
    compare(hit, intersectSphere(snowBall, r));

#if SHOW_COLORED_SPHERES
    float t = abs(fract(iTime * 0.1 - 0.5) * 2.0 - 1.0);
    if (t > 0.5)
    {
        Sphere redSphere = Sphere(0.45, vec3(-1.4, 0.6, 0.0), Material(vec3(0.0), vec3(0.8, 0.1, 0.02), 0.04));
        compare(hit, intersectSphere(redSphere, r));
    }
    if (t > 0.75)
    {
        Sphere whiteSphere = Sphere(0.45, vec3(-1.4, -0.6, 0.0), Material(vec3(0.0), vec3(1.0), 0.04));
        compare(hit, intersectSphere(whiteSphere, r));
    }
    if (t > 0.8)
    {
        Sphere glowingSphere = Sphere(0.45, vec3(1.5, 0.0, 0.0), Material(vec3(0.25, 0.7, 1.0), vec3(0.8), 0.04));
        compare(hit, intersectSphere(glowingSphere, r));
    }
#endif

    return hit;
}

// ---8<----------------------------------------------------------------------
// Lights

vec3 environmentLight(vec3 d)
{
    vec3 uniformEnv = vec3(0.5);

#if SHOW_NON_UNIFORM_ENVIRONMENT
    float t = abs(fract(iTime * 0.05 - 0.75) * 2.0 - 1.0);

    if (d.x * 0.5 + 0.5 < clamp(t * 2.0 - 1.0, 0.0, 1.0))
    {
        return texture(iChannel1, d).rgb;
    }
#endif

    return uniformEnv;
}

// ---8<----------------------------------------------------------------------
// Random number generator

// https://www.pcg-random.org/
uint pcg(uint v)
{
	uint state = v * 747796405u + 2891336453u;
	uint word = ((state >> ((state >> 28u) + 4u)) ^ state) * 277803737u;
	return (word >> 22u) ^ word;
}

uvec2 pcg2d(uvec2 v)
{
    v = v * 1664525u + 1013904223u;

    v.x += v.y * 1664525u;
    v.y += v.x * 1664525u;

    v = v ^ (v>>16u);

    v.x += v.y * 1664525u;
    v.y += v.x * 1664525u;

    v = v ^ (v>>16u);

    return v;
}

// http://www.jcgt.org/published/0009/03/02/
uvec3 pcg3d(uvec3 v) {

    v = v * 1664525u + 1013904223u;

    v.x += v.y*v.z;
    v.y += v.z*v.x;
    v.z += v.x*v.y;

    v ^= v >> 16u;

    v.x += v.y*v.z;
    v.y += v.z*v.x;
    v.z += v.x*v.y;

    return v;
}

// http://www.jcgt.org/published/0009/03/02/
uvec3 pcg3d16(uvec3 v)
{
    v = v * 12829u + 47989u;

    v.x += v.y*v.z;
    v.y += v.z*v.x;
    v.z += v.x*v.y;

    v.x += v.y*v.z;
    v.y += v.z*v.x;
    v.z += v.x*v.y;

	v >>= 16u;

    return v;
}

// http://www.jcgt.org/published/0009/03/02/
uvec4 pcg4d(uvec4 v)
{
    v = v * 1664525u + 1013904223u;

    v.x += v.y*v.w;
    v.y += v.z*v.x;
    v.z += v.x*v.y;
    v.w += v.y*v.z;

    v ^= v >> 16u;

    v.x += v.y*v.w;
    v.y += v.z*v.x;
    v.z += v.x*v.y;
    v.w += v.y*v.z;

    return v;
}

uvec3 seed = uvec3(0);
void initSeed(vec2 fragCoord)
{
