    seed = uvec3(fragCoord, iTime * 1e5);
    seed.zy ^= pcg2d(seed.xz);
}

float rand1()
{
    seed = seed.yzx;
    seed.x = pcg(seed.x);
    return float(seed.x) * (1.0 / float(0xffffffffu));
}

vec2 rand2()
{
    seed = seed.yzx;
    seed.xy = pcg2d(seed.xy);
    return vec2(seed.xy) * (1.0 / float(0xffffffffu));
}

vec3 rand3()
{
    seed = pcg3d(seed);
    return vec3(seed) * (1.0 / float(0xffffffffu));
}

// ---8<----------------------------------------------------------------------
// Sampling

float VanDerCorput(int i)
{
	uint b =  (uint(i) << 16u) | (uint(i) >> 16u);
	b = (b & 0x55555555u) << 1u | (b & 0xaaaaaaaau) >> 1u;
	b = (b & 0x33333333u) << 2u | (b & 0xccccccccu) >> 2u;
	b = (b & 0x0f0f0f0fu) << 4u | (b & 0xf0f0f0f0u) >> 4u;
	b = (b & 0x00ff00ffu) << 8u | (b & 0xff00ff00u) >> 8u;
	return float(b) * 2.3283064365386963e-10; // / 0x100000000
}

//
// [Wong 1997] Sampling with Hammersley and Halton Points
//
vec2 Hammersley(int i, int N)
{
	return vec2(float(i) / float(N), VanDerCorput(i));
}

mat3 basis(vec3 N)
{
    // Hugues-MÃ¶ller algorithm:
    vec3 a = abs(N);

    vec3 T;
    if (a.x <= a.y && a.x <= a.z)
    {
        T = vec3(0, -N.z, N.y);
    }
    else if (a.y <= a.x && a.y <= a.z)
    {
        T = vec3(-N.z, 0, N.x);
    }
    else
    {
        T = vec3(-N.y, N.x, 0);
    }
    T = normalize(T);
    vec3 B = normalize(cross(N, T));

    return mat3(T, B, N);
}

vec3 sphericalToCathesian(float phi, float sinTheta, float cosTheta)
{
	return vec3(
		sinTheta * cos(phi),
		sinTheta * sin(phi),
		cosTheta);
}

vec3 uniformSample(vec2 Xi)
{
    float phi = 2. * PI * Xi.x;
    float cosTheta = Xi.y;
    float sinTheta = sqrt(1. - cosTheta * cosTheta);
	return sphericalToCathesian(phi, sinTheta, cosTheta);
}

vec3 cosineSample(vec2 Xi)
{
    float phi = 2. * PI * Xi.x;
    float sqrSinTheta = Xi.y;
    float sinTheta = sqrt(sqrSinTheta);
    float cosTheta = sqrt(1. - sqrSinTheta);
	return sphericalToCathesian(phi, sinTheta, cosTheta);
}

// ---8<----------------------------------------------------------------------

vec3 BRDF_Lambert(Material m, vec3 N, vec3 L, vec3 E)
{
    return m.c / PI;
}

vec3 radiance(Ray r) {
	vec3 acc = vec3(0.);
	vec3 mask = vec3(1.);

    for (int depth = 0; depth < MAX_DEPTH; ++depth)
    {
        Hit hit = intersectScene(r);
		if (hit == noHit)
        {
            acc += mask * environmentLight(r.d);
            break;
        }

        vec3 N = hit.n;
        vec3 E = -r.d;
        mat3 TStoWS = basis(N);

        vec2 Xi = rand2();

        acc += mask * hit.m.e;

#if SAMPLING == UNIFORM_SAMPLING
        vec3 L = TStoWS * uniformSample(Xi);
        float ndotl = clamp(dot(N, L), 0.0, 1.0);

#if SIMULATE_INCORRECT_INTEGRATION
        // Oh no, we forgot that an hemisphere is 2Pi!
        // The result appears too dark.
        mask *= BRDF_Lambert(hit.m, N, E, L) * ndotl * PI;
#else
        mask *= BRDF_Lambert(hit.m, N, E, L) * ndotl * 2. * PI;
#endif
#elif SAMPLING == COSINE_SAMPLING
        vec3 L = TStoWS * cosineSample(Xi);

#if SIMULATE_INCORRECT_INTEGRATION
        // Oh no, we forgot to change the integrand based on the sampling distribution!
        // The result appears too bright.
        float ndotl = clamp(dot(N, L), 0.0, 1.0);
        mask *= BRDF_Lambert(hit.m, N, E, L) * ndotl * 2. * PI;
#else
        float ndotl_divided_by_pdf = PI;
        mask *= BRDF_Lambert(hit.m, N, E, L) * ndotl_divided_by_pdf;
#endif
#endif // SAMPLING

        if (dot(mask, mask) < 1e-3)
        {
            break;
        }

        r = Ray(hit.t * r.d + r.o + 1e-3 * L, L);
	}
	return acc;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 screenCoord = fragCoord.xy / iResolution.xy;
	vec2 uv = (screenCoord * 2.0 - 1.0) * vec2(1., iResolution.y / iResolution.x);
    vec2 duv = uv / iResolution.xy;
    initSeed(fragCoord);

    vec3 camPos = vec3(0.0, 0.0, -10.0);
    vec3 color = vec3(0.);
	for (int i = 0; i < SAMPLES_PER_FRAME + min(0, iFrame); ++i)
    {
        vec3 rd = normalize(vec3(uv + duv * Hammersley(i, SAMPLES_PER_FRAME), 5.0));
		color += radiance(Ray(camPos, rd));
    }

#if ENABLE_ACCUMULATION
    vec4 accum = texture(iChannel0, screenCoord);
    float prev_samples = min(accum.a, float(MAX_ACCUMULATION_SAMPLES - SAMPLES_PER_FRAME));
    float samples = prev_samples + float(SAMPLES_PER_FRAME);
	fragColor = vec4((accum.rgb * prev_samples + color) / samples, samples);
#else
    color /= float(SAMPLES_PER_FRAME);
	fragColor = vec4(color, 1.);
#endif
}

