// Projective Box, mla, 2023
//
// Draw a 3d box undergoing projective transformation, ie. a 4x4 matrix acting
// on homogeneous coordinates. In particular, this means that the box can
// be bisected by the plane at infinity and appear as two parts.
//
// A fairly dumb raytracing approach - compute analytic intersection with
// the six box sides and find nearest. Cleverer ideas welcome.
//
// There is a check that the normals are correctly calculated from the
// R4 coordinates - compute normals for two nearby points and assert if
// they are signicantly different (this of course happens when they are
// separated by an edge).
//
// a: centre view
// f: fog
// g: grid
// n: check normals
// t: texture

float sgn(float x) {
  if (x <= 0.0) return -1.0;
  else return 1.0;
}

// Return distance to x plane, also uv and the plane itself
float xplane(vec4 p, vec4 r, float w, out vec2 uv, out vec4 plane) {
  plane = vec4(1,0,0,w);
  float t = -dot(p,plane)/dot(r,plane);
  vec4 q = p+t*r;
  uv = vec2(-sgn(w),1)*q.yz/q.w;
  return t;
}

// Same for y and z planes
float yplane(vec4 p, vec4 r, float w, out vec2 uv, out vec4 plane) {
  plane = vec4(0,1,0,w);
  float t = -dot(p,plane)/dot(r,plane);
  vec4 q = p+t*r;
  uv = vec2(-sgn(w),1)*q.zx/q.w;
  return t;
}

float zplane(vec4 p, vec4 r, float w, out vec2 uv, out vec4 plane) {
  plane = vec4(0,0,1,w);
  float t = -dot(p,plane)/dot(r,plane);
  vec4 q = p+t*r;
  uv = vec2(-sgn(w),1)*q.xy/q.w;
  return t;
}

float iBox(vec4 p, vec4 r, out vec2 uv, out vec4 plane) {
  float t = 1e8;
  for (float w = -1.0; w <= 1.0; w += 2.0) {
    float t1; vec2 uv1; vec4 plane1;
    t1 = xplane(p,r,w,uv1,plane1);
    if (t1 > 0.0 && t1 < t && max(abs(uv1.x),abs(uv1.y)) < 1.0) {
      t = t1; uv = uv1; plane = plane1;
    }
    t1 = yplane(p,r,w,uv1,plane1);
    if (t1 > 0.0 && t1 < t && max(abs(uv1.x),abs(uv1.y)) < 1.0) {
      t = t1; uv = uv1; plane = plane1;
    }
    t1 = zplane(p,r,w,uv1,plane1);
    if (t1 > 0.0 && t1 < t && max(abs(uv1.x),abs(uv1.y)) < 1.0) {
      t = t1; uv = uv1; plane = plane1;
    }
  }
  if (t == 1e8) return -1.0;
  return t;
}

