        vec3 rd0 = normalize(transform(vec3(0,0,-2)));
        vec3 normal0, normal1;
        float t0 = scene(vec4(ro,1),vec4(rd0,0),uv,normal0);
        if (t0 > 0.0) {
          vec3 rd1 = normalize(transform(vec3(0,0.1,-2)));
          float t1 = scene(vec4(ro,1),vec4(rd1,0),uv,normal1);
          assert(length(normal0-normal1) < 1e-4);
          vec3 p0 = ro+t0*rd0;
          vec3 p1 = ro+t1*rd1;
          col *= sphere(ro,rd,p0,0.01);
          col *= sphere(ro,rd,p1,0.01);
          assert(abs(dot(normal0,p1-p0)) < 1e-4);
        }
      }
      aacol += col;
    }
  }
  aacol /= float(AA*AA);
  aacol = pow(aacol,vec3(0.4545)); // gamma correction
  if (alert) aacol.r = 1.0;
  fragColor = vec4(aacol,1);
}

