// Buffered fbm
float bfbm(vec2 x)
{
    return texture(iChannel0, x).r;
}

// Inspired by https://iquilezles.org/articles/warp/
float pattern(vec2 x)
{
    // Because of the tiled nature of bfbm, I need to be
    // carful when it comes to sizing the sampling coordinate
    // x, if I want to avoid evident non-randomness in the
    // result due to tiling
    x*=.25;
    vec2 a = vec2(bfbm(2.*x+iTime/7.), 2.3*bfbm(.2*x+iTime/8.));
    float b = bfbm(x+.25*a-.1*iTime);
    vec2 c = vec2(bfbm(.5*x+2.*b), bfbm(x+.25*a));
    return pow(bfbm(x+.75*c), 1./1.8);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec2 d = 3.*vec2(2.+cos(iTime/10.), 3.+sin(iTime/100.));
    fragColor = vec4(vec3(pattern(3.*uv-d)), 1.);
}

