float random(vec2 x)
{
    return fract(138912.*sin(dot(x, vec2(138.9, 191.2))));
}

// A 2D noise implementation I came up with that requires 
// one less call to the pseudo-random number generator and
// one less mixing. Should be faster, (albeit more biased?), 
// than the standard Perlin noise. Easily extandable to 3D
float triNoise(vec2 x)
{
    vec2 l = floor(x);
    vec2 r = fract(x);
    float s = float(int(r.x+r.y > 1.));
    vec2 e = vec2(1.,0.);
    float a = random(l+s*e.yx);
    float b = random(l+s*e.yx+e.xy);
    float c = random(l+s*e.xy+(1.-s)*e.yx);
    r.y = s+r.y*(1.-2.*s);
    r.x = (r.x-s*r.y)/(1.-r.y);
    r *= r*(3.-2.*r); // Same cubic profile as smoothstep
    return mix(mix(a, b, r.x), c, r.y);
}

// Fractional Brownian Motion noise
float fbm(vec2 x)
{
    float n = 0.;
    float A = 0.;
    vec2 af = vec2(1., 2.);
    for (int i=0; i<10; i++)
    {
        // Rotate each octave
        float s = sin(float(2*i));
        float c = cos(float(2*i));
        mat2 m = mat2(c, s, -s, c);
        n += af.x*triNoise(af.y*m*x);
        A += af.x;
        af *= vec2(.45,2.);
    }
    return n/A;
}

buffer_a_code:
// Here, the fbm noise is compute once and once only, and then
// used by all other applications (e.g., but not done here, an SDF).
// This can result in massive performance gains, but the main issue
// is that this pre-computed noise will only be 'random' within a
// tileable region of the buffer. This introduces some annoying
// limitations to its usage, but if sampled carefully, it can be
// a game-changer for very noise-heavy shaders

// Keep in mind that this is buffered 2D noise. For 3D, you would 
// either need to use a Cubemap buffer, or devise a 
// continuous/seamless 3D-to-2D mapping

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Make the noise tileable
    // If only ShaderToy allowed a mirror & repeat wrap option for
    // the channel, I wouldn't have to do this, which unfortunately
    // also reduces the actual size of the 'random' part of the
    // texture by a factor of 4
    if (uv.x>.5)
        uv.x = 1.-uv.x;
    if (uv.y>.5)
        uv.y = 1.-uv.y;

    // Totally not necessary, just a smoothing of uv in [0.,.5]
    // which by extensions ends up also smoothing the fbm
    uv*=uv*(3.-4.*uv); 

    // Calc fbm and store
    float n = fbm(uv+.1); // The .1 is also totally not necessary
    fragColor = vec4(n,1.,1.,1.);
}

