// Constants
#define PI 3.1415925359
#define MAX_STEPS 100// Mar Raymarching steps
#define MAX_DIST 100.// Max Raymarching distance
#define SURF_DIST .01// Surface Distance

// GetDist
float GetDist(vec3 p) {
    float d=0.;

    vec3 bPos = p + vec3(0, sin(iTime * 0.25), 0);
    bPos.xy *= Rotate(iTime * 0.25);
    bPos.xz *= Rotate(iTime * 0.5);


    d = roundBoxSDF(bPos, vec3(.5, .5, .5), .1);
    // d = boxSDF(bPos, vec3(0.1));

    // Plane
    float planeDist = p.y;
    d = smoothUnionSDF(d, planeDist, smoothstep(1.,0.,p.y));

    return d;
}

// RayMarch
float RayMarch(vec3 ro,vec3 rd)
{
    float dO=0.;//Distane Origin
    for(int i=0;i<MAX_STEPS;i++)
    {
        vec3 p = ro + rd*dO;
        float ds = GetDist(p);// ds is Distance Scene
        dO += ds;
        if(dO>MAX_DIST||ds<SURF_DIST)
        break;
    }
    return dO;
}

// GetNormal
vec3 GetNormal(vec3 p) { 
  float d = GetDist(p); // Distance
  vec2 e = vec2(.01,0); // Epsilon

  vec3 n = d - vec3(
    GetDist( p - e.xyy),  // e.xyy is the same as vec3(.01,0,0).
    GetDist( p - e.yxy),
    GetDist( p - e.yyx));

  return normalize(n);
}

 // GetLight
float GetLight(vec3 p) { 
  // Directional light
  vec3 lightPos = vec3(5.*sin(iTime),5.,5.0*cos(iTime)); // Light Position
  vec3 l = normalize(lightPos - p); // Light Vector
  vec3 n = GetNormal(p); // Normal Vector

  float dif = dot(n, l); // Diffuse light
  dif = clamp(dif, 0., 1.); // Clamp so it doesnt go below 0

  // Shadows
  float d = RayMarch(p+n*SURF_DIST*2., l); 

  if(d<length(lightPos-p)) dif *= .1;
  return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = (2.*fragCoord.xy-iResolution.xy)/iResolution.x;

    vec3 ro = vec3(0, 1, -3.5);// Ray Origin/Camera
    vec3 rd = normalize(vec3(uv2 ,1));// Ray Direction

    float d = RayMarch(ro,rd);// Distance

    vec3 p = ro + rd * d;
    float dif = GetLight(p); // Diffuse lighting

    vec3 color = vec3(dif);

    fragColor = vec4(color, 1.0);
}

