float sdBox( vec2 p, vec2 b){ //source: https://iquilezles.org/articles/distfunctions2d/
    vec2 d = abs(p) - b;
    return length(max(d,.0)) + min(max(d.x,d.y),.0);
}

float sdEquilateralTriangle(vec2 p){ //source: https://iquilezles.org/articles/distfunctions2d/
    const float k = sqrt(3.);
    p.x = abs(p.x) - 1.;
    p.y = p.y + 1./k;
    if (p.x+k*p.y > 0.) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.;
    p.x -= clamp( p.x, -2., 0.);
    return -length(p)*sign(p.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord){

    compressFft(); //initializes fft, ffts
    vec2 uv = (2.*fragCoord-iResolution.xy) / max(iResolution.x, iResolution.y); //long edge -1 to 1, square aspect ratio
    uv *= .6 + .5*getDat(dot(floor((uv+1.)/.1),vec2(1,10))+24.); //pixel shift
    float widthF = (isVdj)? 9. : 1.;

    vec3 r3 = vec3(getDat(0),getDat(1),getDat(2));
    vec3 col = (.3+.5*pow(fft.x,9.)) * (r3*r3*r3*r3*r3*vec3(3,1,5)*.005+.01) * widthF / abs(vec3( //draw glowing symbols
        sdCircle( uv, .27*r3.x),
        sdEquilateralTriangle( uv/(.1/r3.y+.001)) * .1/r3.y,
        sdBox( uv, vec2(.35*r3.z))) );

    uv /= (.3+.7*pow(fft.w,.5)); //background zoom
    col = (col*4.+.6) * vec3(.5*fft.z+.2) * getDat((floor(abs(uv.y)*22.)+floor(abs(uv.x)*22.)/22.)/22.*512.); //pixel background

    float colId = 3. * floor(mod(aTime/16.,5.)); //color set id 
    col = mat3( getCol( colId+0.), getCol( colId+1.), getCol( colId+2.)) * col; //remap colors
    col *= abs( cos( .06*iTime + PI/vec3(.5,2.,4.) + ffts.xyz*PI)); //color shift 

    fragColor = vec4(col,1.);
}

// Based on Single-pass gaussian blur - fast 
// By FabriceNeyret2 
// Reference: https://www.shadertoy.com/view/ltScRG
const int   Samples  = 8; // Actual sample count is Samples / (2^(LODLevel))
const int   LODLevel = 1;
const float Sigma    = float(Samples) * .25;

float gaussian(vec2 i) 
{
    i /= Sigma;
    return exp(-.5 * dot(i, i) ) / (Tau * Sigma * Sigma);
}

vec4 bloom(sampler2D channel, vec2 uv) 
{
    vec4 color    = vec4(0.);
    int  sampleNb = Samples / (1 << LODLevel);
    vec2 stepSize = 1. / iChannelResolution[1].xy;
    float sLOD    = float(1 << LODLevel);
    for(int i = 0; i < sampleNb; i++)
    {
        for(int j = 0; j < sampleNb; j++)
        {
            vec2 delta       = vec2(i, j) * sLOD - float(Samples) * .5;
            vec2 coordinates = uv + stepSize * delta;

            vec4 sampleData  = textureLod(channel, coordinates , float(LODLevel) );
            vec3 sampleColor = sampleData.rgb * float(sampleData.w < 0.);

            color += gaussian(delta) * vec4(sampleColor, 1.);
        }
    }

    return color / color.a;
}

// ACES tonemapping from romainguy's shader
// Reference: https://www.shadertoy.com/view/llXyWr
vec3 ACESFilm( vec3 x )
{
	float a = 2.51f;
	float b = 0.03f;
	float c = 2.43f;
	float d = 0.59f;
	float e = 0.14f;
	return clamp( ( x * ( a * x + b ) ) / ( x * ( c * x + d ) + e ), vec3( 0.f ), vec3( 1.f ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{       
    vec4 color = vec4(0.);

    vec2 uv = fragCoord + .5;
    uv = uv - iResolution.xy * .5;
    uv = uv / iResolution.y;

    // Chromatic aberration on sides of the image while preserving candles
    vec3 offset = max(0., length(uv - vec2(.25, 0.1)) - .2) * (vec3(8.5, 5.9, .8) / 1280. * iResolution.x);
    color.r = texture(iChannel0, (fragCoord + offset.x) / iResolution.xy).r;
    color.g = texture(iChannel0, (fragCoord + offset.y) / iResolution.xy).g;
    color.b = texture(iChannel0, (fragCoord + offset.z) / iResolution.xy).b;

    // Apply bloom only if pixel is non emissive
    vec4 bloom = bloom(iChannel0, fragCoord / iResolution.xy);
    color     += bloom * 1.1;
    color.rgb *= 1.5;

    // Tonemapping & Gamma Correction
    fragColor = vec4(pow(ACESFilm(color.rgb), vec3(1. / 2.2)), 1.f);

    uv = (fragCoord / iResolution.xy - .5) * 2.;
    bool startAnimation = abs(uv.y) > min(iTime * .6, .8) || abs(uv.x) > min(iTime * .75, 1.);
    bool endAnimation   = (1. - abs(uv.x)) <= clamp(iTime * .25 - 39. / 4., 0., 1.)
                       || (1. - abs(uv.y)) <= clamp(iTime * .25 - 39. / 4., 0., 1.);
    if(startAnimation || endAnimation) 
    {
        fragColor = vec4(vec3(0.), 1.f);
        return;
    }

    // Vignette
    fragColor *= 1. - .2 * dot(uv, uv); 

    // Overlay
    vec4 overlay  = texture(iChannel1, fragCoord / iResolution.xy);
    fragColor.rgb = mix(fragColor.rgb, overlay.rgb, overlay.w);
    fragColor.w = 1.;
}

