// Based on GLM implementation
vec3 mul(vec4 quat, vec3 point)
{
	vec3 quatVector = vec3(quat.x, quat.y, quat.z);
	vec3 uv = cross(quatVector, point);
	vec3 uuv = cross(quatVector, uv);

	return point + ((uv * quat.w) + uuv) * 2.;
}

