// GLSL Torus Knot from Syntopia and DarkBeam
// Reference: https://www.fractalforums.com/new-theories-and-research/not-fractal-but-funny-trefoil-knot-routine/15/
float deTorusKnot(vec3 p, vec3 r, vec2 c, float polyfoldOrder) 
{
	float mobius = (c.x + c.y/polyfoldOrder) * atan(p.y,p.x);
	p.x = length( p.xy ) - r.x;
	rotate(p.xz, mobius);	

    float m     = polyfoldOrder / Tau;
	float angle = floor(.5 + m * (Pi/2. - atan(p.x,p.z))) / m;

	rotate(p.xz, angle);
	p.x = p.x - r.z;

    return (length(p.xz)-r.y) * .8; // Slightly overestimated when radius is small
}

//-- Random
// Reference: https://www.shadertoy.com/view/XlGcRh
// Hash Functions for GPU Rendering. Mark Jarzynski, & Marc Olano (2020).
// Journal of Computer Graphics Techniques (JCGT), 9(3), 20â38.
uvec4 pcg4d(uvec4 v)
{
    v = v * 1664525u + 1013904223u;

    v.x += v.y * v.w;
    v.y += v.z * v.x;
    v.z += v.x * v.y;
    v.w += v.y * v.z;

    v ^= v >> 16u;

    v.x += v.y * v.w;
    v.y += v.z * v.x;
    v.z += v.x * v.y;
    v.w += v.y * v.z;

    return v;
}

vec4 prng(inout uvec4 p)
{
    p.w++;
    return vec4(pcg4d(p)) * (1.0/float(0xffffffffu));
}

//-- Hashes
// From: Quality hashes collection WebGL2 by nimitz 
// Reference: https://www.shadertoy.com/view/Xt3cDn
// Modified from: iq's "Integer Hash - III" (https://www.shadertoy.com/view/4tXyWN)
uint baseHash(uvec3 p)
{
    p = 1103515245U*((p.xyz >> 1U)^(p.yzx));
    uint h32 = 1103515245U*((p.x^p.z)^(p.y>>3U));
    return h32^(h32 >> 16);
}
uint baseHash(uint p)
{
    p = 1103515245U*((p >> 1U)^(p));
    uint h32 = 1103515245U*((p)^(p>>3U));
    return h32^(h32 >> 16);
}

float hash11(uint x)
{
    uint n = baseHash(x);
    return float(n)*(1.0/float(0xffffffffU));
}

vec3 hash31(uint x)
{
    uint n = baseHash(x);
    uvec3 rz = uvec3(n, n*16807U, n*48271U); //see: http://random.mat.sbg.ac.at/results/karl/server/node4.html
    return vec3((rz >> 1) & uvec3(0x7fffffffU))/float(0x7fffffff);
}

float hash13(uvec3 x)
{
    uint n = baseHash(x);
    return float(n)*(1.0/float(0xffffffffU));
}

vec3 hash33(uvec3 x)
{
    uint n = baseHash(x);
    uvec3 rz = uvec3(n, n*16807U, n*48271U); //see: http://random.mat.sbg.ac.at/results/karl/server/node4.html
