DrawChar defaultDrawChar() { return DrawChar(-1, vec2(-1.), vec2(-1.), vec2(-1.)); }

void writeChar( int id, inout vec2 uv, inout DrawChar char ) 
{
    uv.x -= .5;

    vec2 dFdx = dFdx( uv / 16. ), dFdy = dFdy( uv / 16. );
    if ( uv.x > .25 && uv.x < .75 && uv.y > .1 && uv.y < .85 )
        char = DrawChar( id, uv, dFdx, dFdy );
