//-- Textures utils
vec2 toroidalMapping(vec3 p, vec2 r, float k) 
{
    // Toroidal mapping
    // Reference: https://www.shadertoy.com/view/sdd3R4
    float a = atan(p.z, p.x + 1e-15);
    float b = atan(p.y, (length(p.xz) - r.x) + 1e-15);
    return vec2(mod(k * r.x * a / Pi, r.x), mod(1. + k * r.y * b / Tau, r.y));
}

float draw(float d, float w) { return smoothstep(.15, -.15, (length(w - d) - 2.5e-2)); }
float truchetLabyrinth(vec2 uv) 
{
    float Width = 11.;

    const float Radius    = .5;
    const float LineWidth = 1e-2;

    vec2 id = uv * Width;
    uv      = mod(uv, vec2(1. / Width)) * Width ;

    // Random tile orientation
    float angle = Pi*.5 * floor(hash13(uvec3(id.x, id.y, 1)) * 3.);
    uv -= .5;
    rotate(uv, angle);
    uv += .5;

    // [0, 1] -> [-1, 1]
    uv *= 2.;
    uv -= 1.;

    // Draw middle line
    float l = abs(dot(uv, vec2(1.))) ;
    return draw(abs(l * .5) - 1e-1, LineWidth);
}

float truchetArcs(vec2 uv) 
{       
    float Width           = 9.;
    const float Radius    = .5;
    const float LineWidth = 1e-1;

    // Tiling
    vec2 id = uv * Width;
    uv      = mod(uv, vec2(1. / Width)) * Width ;

    // Random tile orientation
    float angle = Pi*.5 * floor(hash13(uvec3(id.x, id.y, 2)) * 3.);
    uv -= .5;
    uv  = mat2(
        cos(angle), -sin(angle), 
        sin(angle), cos(angle)
    ) * uv;
    uv += .5;

    // Draw circle parts
    float l = length(uv - vec2(1.)) - Radius;
    l = min(l, length(uv - vec2(0.)) - Radius);

    return draw(abs(l * 2.5), LineWidth);
}

vec3 jfig(vec2 uv, sampler2D charTexture) 
{
    const vec3 MainColor      = vec3( 0.67, 0.81, 0.34 );
    const vec3 SecondaryColor = vec3( .5 );
    const vec3 Background     = vec3( 0. );

    // [0, 1] -> [-1, 1] & Scale
    uv = uv * 2. - 1.;
    uv *= 1.8;

    // Logo
    float d  = length(uv) - 1.;    
    float d2 = length(uv - vec2(.7, -.5)) - .25;
    float d3 = length(uv - vec2(1.1, -.75)) - .125;

    vec3 color = Background;
    if(-.3 < d && d < 0. && abs(-uv.x + uv.y) > 0.1 && abs(-uv.x - uv.y) > 0.1)
        color = mix(MainColor, SecondaryColor, float(-uv.x + uv.y > 0.));

    if(d2 < 0.05)
        color = mix(MainColor, Background, float(d2 > 0.));
    else if(d3 < 0.)
        color = MainColor;

    // Text part: "AFIG 30 ANS"
    DrawChar char  = defaultDrawChar();
    vec2 textStart = uv * .81 - vec2(-1.05, 0.2);
    vec2 charUv    = textStart * 0.78 - vec2(1.01, -.32);

#define writeLetter( id ) writeChar( letter( id ), charUv, char ) 
#define writeNumber( id ) writeChar( number( id ), charUv, char ) 
    writeLetter( 1 ); writeLetter( 6 ); writeLetter( 9 ); writeLetter( 7 );

    charUv = ( textStart * 1.07 - vec2(1.65, -1.2) );
    writeNumber(3); writeNumber(0); charUv.x -= .2; writeLetter( 1 ); writeLetter( 14 ); writeLetter( 19 );
#undef writeLetter
#undef writeNumber

    color += float(drawChar(char, charTexture).x > 0.5) * MainColor;
    return color;
}

//-- Reprojection utils
vec2 getUV(vec3 position, vec2 resolution, vec3 mouse, int frame, float time, sampler2D noiseTexture)
{
    vec4 rotation = normalize(quat( time * .2, vec3(0., 1., 0.)));
    vec3 ro = vec3( 5.75, 0., sin(time) * .2);
    ro      = mul(rotation, ro);

    vec3 rd  = normalize(position - ro);

    if( mouse.z > 0. )
    {
        float moveSize = -mouse.x / resolution.x;
        rotation = normalize(quat(Pi - moveSize * Tau, vec3(0., 1., 0.)));
        rd       = mul(conjugate(rotation), rd);
    }

    rotation = normalize(quat(1.15 * Pi + time * .2, vec3(0., 1., 0.)));
    rd       = mul(conjugate(rotation), rd);

    const float focalLength = tan(Pi * .25);

    // Reference: https://www.shadertoy.com/view/wtcXz4
    vec2 uv2 = rd.xy * focalLength / rd.z;

    // 4. [ -.5, .5 ] -> [vec2(-iResolution.xy * .5 + .5), iResolution.xy * .5 + .5] 
    uv2 = uv2 * resolution.y;

    // 3. [vec2(.5), iResolution.xy + .5] -> [vec2(-iResolution.xy * .5 + .5), iResolution.xy * .5 + .5]
    uv2 = uv2 + resolution.xy * .5;

    // 2. Apply jittering [vec2(0), iResolution.xy]  
    vec2 jitter = getBlueNoise(uv2, frame, noiseTexture).xy;
    uv2         = uv2 - (jitter * .5 - .5);

    // 1. Center of pixels [vec2(0), iResolution.xy] -> [vec2(0.5), iResolution.xy + .5] 
    uv2 = uv2 - .5;

    return uv2;
}

void getCamera(vec2 fragCoord, vec2 resolution, vec3 mouse, int frame, float time, sampler2D noiseTexture, out vec3 ro, out vec3 rd) 
{    
    // 1. Center of pixels [vec2(0), iResolution.xy] -> [vec2(0.5), iResolution.xy + .5] 
    vec2 uv = fragCoord + .5;

    // 2. Apply jittering [vec2(0), iResolution.xy]  
    vec2 jitter = getBlueNoise(fragCoord, frame, noiseTexture).xy;
    uv = fragCoord + (jitter * .5 - .5);

    // 3. [vec2(.5), iResolution.xy + .5] -> [vec2(-iResolution.xy * .5 + .5), iResolution.xy * .5 + .5]
    uv = uv - resolution.xy * .5;

    // 4. [vec2(-iResolution.xy * .5 + .5), iResolution.xy * .5 + .5] -> [ -.5, .5 ]
    uv = uv / resolution.y;

    const float focalLength = tan(Pi * .25);

    rd = normalize(vec3(uv, focalLength));
    ro = vec3( 5.75, 0., sin(time) * .2);

    vec4 rotation = normalize(quat(time * .2, vec3(0., 1., 0.)));
    ro = mul(rotation, ro);

    rotation = normalize(quat(1.15 * Pi + time * .2, vec3(0., 1., 0.)));
    rd = mul(rotation, rd);

    if( mouse.z > 0. )
    {
        float moveSize = -mouse.x / resolution.x;
        rotation = normalize(quat(Pi - moveSize * Tau, vec3(0., 1., 0.)));
        rd       = mul(rotation, rd);
    }
}

