// -- Pathtracing pass

const float SampleNb = 1.;
const int   BounceNb = 2;

const float ReprojectionRadius       = 2.;
const float ReprojectionWeightMoving = .1;
const float ReprojectionMaxDistance  = 25.;

// The scene evaluation is split into two parts: Candles and Neons. 
// These evaluations are themselves defined by three functions: light sampling, 
// distance estimation, and material evaluation.

// Neons part
LightSample sampleLightNeon(vec3 p, vec4 u) 
{
    vec3  position, wi, strength;
    float pdf;
    if( u.x > 0.5 ) 
    {
        // Fake candle's fire light with spherical light above them
        const float repetitions = 30.;
        const float start       = -6. * Pi;
        float stepSize          = 2.*Pi/repetitions;

        // pModPolar id and sample neighborhing lights
        float angle = atan(p.z, p.x + 1e-15) + stepSize * .5 + stepSize * .5 * (2. * u.y - 1.);
        float r     = length(p);
        float id    = floor(angle/stepSize);
        if (abs(id) >= (repetitions/2.)) id = abs(id);

        strength = 100. * rndColor(floatBitsToUint(id) + 10u);

        position      = vec3(cos(angle) * 4.5, .65, sin(angle) * 4.5);
        vec3 n        = normalize(position - p);
        vec4 rotation = toLocalZ(n);

        const float Radius = .1;
        float sinThetaMax2 = Radius * Radius / max(1e-4, length2(p - position));
        float cosThetaMax  = sqrt(max(0., 1. - sinThetaMax2));

        wi = mul(conjugate(rotation), sampleCone(cosThetaMax,  u.zw, pdf));
    }
    else 
    {
        const float Radius = 1.;

        vec4 rotation = normalize(quat( Pi/3. + iTime * .2, vec3(0., 1., 0.)));
        position      = vec3( 6., 0., 0.);
        position      = mul(rotation, position);
        vec3 n        = normalize(position - p);
        rotation      = toLocalZ(n);
        strength      = 5. * normalize(p - position);

        // Spherical light sampling from PBRT
        // By: Matt Pharr, Wenzel Jakob, Greg Humphreys
        // Reference: https://pbr-book.org/3ed-2018/Light_Transport_I_Surface_Reflection/Sampling_Light_Sources#fragment-Computecoordinatesystemforspheresampling-0
        float sinThetaMax2 = Radius * Radius / max(1e-4, length2(p - position));
        float cosThetaMax  = sqrt(max(0., 1. - sinThetaMax2));

        wi = mul(conjugate(rotation), sampleCone(cosThetaMax,  u.zw, pdf));
    }

    return LightSample(position, wi, strength, pdf);
}

float sdSceneNeon(vec3 position) 
{
    vec3  r = vec3(5., (abs(sin(iTime)) * 1. + 2.) * 0.02, 2.);
    vec2  c = vec2(3. + 1. * abs(sin(iTime  * .5)), 5.);
    float t = 45.;
    float d = deTorusKnot(position.xzy, r, c, t);

    // Toroidal mapping
    // Reference: https://www.shadertoy.com/view/sdd3R4
    float R0 = 5., R1 = 2., k = 5.;
    float a  = atan(position.z, position.x + 1e-15);
    float dr = length(position.xz)-R0;
    float b  = atan(position.y, dr + 1e-15);

    float u = mod( k * R0 * a / Tau, Pi * .5);
    float v = k * R1 * b/6.283;
    vec2 uv = vec2( 1. - (.1 + u), .25 +v);
    if(abs(position.y) < .45 && dr > 1.) 
        d = abs(sdTorus(position, vec2(5., 2.)));

    // Candles repetition
    const float repetitions = 30.;
    float id     = pModPolar(position.xz , repetitions);
    position.y  -= .2 * sin(iTime * 2. + 2. * hash11(floatBitsToUint(id)));
    position.xz -= vec2(4.5 , 0.);

    rotate(position.xz, iTime);

    // A single candle
    float height = .3  + .15 * (hash11(floatBitsToUint(16. + id)) * 2. - 1.);;
    float radius = 0.03;

    position.y -= .1;
    float d1 = sdCappedCylinder(position, height, radius * 3. );

    return min(d, d1);
}

Material sceneMaterialNeon(vec3 position) 
{
    Material material = defaultMaterial();

    vec3 pp = position;

    vec3  r = vec3(5., (abs(sin(iTime)) * 2. + 2.) * 0.02, 2.);
    vec2  c = vec2(2. + 2. * abs(sin(iTime  * .5)), 5.);
    float t = 45.;
    float d = deTorusKnot(pp.xzy, r, c, t);

    material.baseColor = vec3(.7, .5, .5);
    material.emission  = vec3(.0);

    // Toroidal mapping
    // Reference: https://www.shadertoy.com/view/sdd3R4
    float R0 = 5., R1 = 2., k = 5.;
    float a  = atan(pp.z, pp.x + 1e-15);
    float dr = length(pp.xz) - R0;
    float b  = atan(pp.y, dr + 1e-15);

    float u = mod( k * R0 * a / Tau, Pi );
    float v = k * R1 * b / Tau;
    vec2 uv = vec2( 2. - (.1 + u), .25 +v);
    if(abs(pp.y) < .45 && dr > 1.) 
    {
        d = abs(sdTorus(pp, vec2(5., 2.)));

        vec3 color         = jfig(uv + .25, iChannel3);
        material.baseColor = color;
        material.emission  = .5 * color;
    }

    // Candles repetition
    const float repetitions = 30.;
    float id     = pModPolar(position.xz , repetitions);
    position.y  -= .2 * sin(iTime * 2. + 2. * hash11(floatBitsToUint(id)));
    position.xz -= vec2(4.5 , 0.);

    rotate(position.xz, iTime);

    // A single candle
    float height = .3  + .15 * (hash11(floatBitsToUint(16. + id)) * 2. - 1.);;
    float radius = 0.03 + .3 * (hash11(floatBitsToUint(17. + id)) * 2. - 1.);

    position.y -= .1;
    float d1 = sdCappedCylinder(position, height, radius * 3. );
    if(d1 < d)
    {
        d = d1;

        material.baseColor = rndColor(floatBitsToUint(id) + 10u);
        material.emission  = float(position.y > 0.1) * rndColor(floatBitsToUint(id) + 10u);
    }

    return material;
}

LightSample sampleLightCandles(vec3 p, vec4 u) 
{
    vec3  position, wi, strength;
    float pdf;

    // Fake candle's fire light with spherical light above them
    const float repetitions = 30.;
    const float start       = -6. * Pi;
    float stepSize          = 2.*Pi/repetitions;

    // pModPolar id and sample neighborhing lights
    float angle    = atan(p.z, p.x + 1e-15) + stepSize/2. + stepSize * (u.y * 2. - 1.);
    float r        = length(p);
    float id       = floor(angle/stepSize);
    if (abs(id) >= (repetitions/2.)) id = abs(id);

    strength = 300. * rndColor(floatBitsToUint(id) + 10u);

    position      = vec3(cos(angle) * 4., .5 + .5 * (hash11(floatBitsToUint(16. + id)) * 2. - 1.) + .2 * sin(iTime + 2. * hash11(floatBitsToUint(id))), sin(angle) * 4.25);
    vec3 n        = normalize(position - p);
    vec4 rotation = toLocalZ(n);

    // Spherical light sampling from PBRT
    // By: Matt Pharr, Wenzel Jakob, Greg Humphreys
    // Reference: https://pbr-book.org/3ed-2018/Light_Transport_I_Surface_Reflection/Sampling_Light_Sources#fragment-Computecoordinatesystemforspheresampling-0
    const float Radius = .05;
    float sinThetaMax2 = Radius * Radius / max(1e-4, length2(p - position));
    float cosThetaMax  = sqrt(max(0., 1. - sinThetaMax2));

    wi = mul(conjugate(rotation), sampleCone(cosThetaMax,  u.zw, pdf));

    return LightSample(position, wi, strength, pdf);
}

// Truchet Candles
float sdSceneCandles(vec3 position)
{
    // Environment
    vec2 torusDimensions = vec2(5., 2.);
    float d = abs(sdTorus(position, torusDimensions));

    // Logo rolling sphere from torus parametrization
    float sphereRadius = .5;
    float phi          = -.75 * (iTime - 10.);
    float theta        = 2. * (iTime - 10.);
    vec3 spherePosition = vec3(
        (torusDimensions.x + (torusDimensions.y - sphereRadius) * cos(theta)) * cos(phi),
        (torusDimensions.y - sphereRadius) * sin(theta),
        (torusDimensions.x + (torusDimensions.y - sphereRadius) * cos(theta)) * sin(phi)
    );

    d = min(d, length(position - spherePosition) - sphereRadius);

    for(float i = 0.; i < 3.; i++) 
    {
        sphereRadius = .2;
        phi          = -.75 * (iTime - 10. - i * .3);
        theta        = 2. * (iTime - 10. - i * .3);
        spherePosition = vec3(
            (torusDimensions.x + (torusDimensions.y - sphereRadius) * cos(theta)) * cos(phi),
            (torusDimensions.y - sphereRadius) * sin(theta),
            (torusDimensions.x + (torusDimensions.y - sphereRadius) * cos(theta)) * sin(phi)
        );

        d = min(d, length(position - spherePosition) - sphereRadius);
    }

    // Candles repetition
    const float repetitions = 30.;
    float id     = pModPolar(position.xz , repetitions);
    position.y  -= .2 * sin(iTime * 2. + 2. * hash11(floatBitsToUint(id)));
    position.xz -= vec2(4.5 , 0.);

    rotate(position.xz, iTime);

    // A single candle
    float height = .3  + .15 * (hash11(floatBitsToUint(16. + id)) * 2. - 1.);;
    float radius = 0.005;

    // Holder
    float holderRadius = radius * hash11(floatBitsToUint(id));
    float holderHeight = .06 * hash11(floatBitsToUint(id));
    float holder = sdCappedCylinder(
        position + vec3(0., height * .5 + holderHeight, 0.), 
        holderHeight, 
        holderRadius
    );

    holderRadius = radius + .1;
    holderHeight = .01;
    d = min(d, sdCappedCylinder(
        position + vec3(0., height * .5 + holderHeight, 0.), 
        holderHeight, 
        holderRadius
    ));
    d = min(d, sdCappedCylinder(
        position + vec3(0., height * .5 + holderHeight, 0.), 
        holderHeight * 6., 
        holderRadius * .5
    ));

    // Candle
    position.y -= .1;
    float d1 = sdCappedCylinder(position, height, radius * 3. );

    // Twisted effect from a box
    vec4 qq = quat( 15. * position.y, vec3(0., 1., 0.));
    vec3 q  = mul(qq, position);

    radius *= 5. + 4. * (hash11(floatBitsToUint(15. + id)) * 2. - 1.);
    float d2 = sdBox(q, vec3(radius, height - .07, radius));
    d2 -= .02; // Smoothen corners

    float candle = opSmoothUnion(d1, d2, .1);
    d            = min(d, candle);

    // Flame
    position.xz += .002 * sin(position.y * 2. + 10. * iTime + 10. * id);
    float oscillatingSphere = length(position - vec3(0., height, 0.)) - 0.015 - sin(iTime) * 0.005;
    float dFire             = opSmoothUnion(
        sdCappedCylinder(position - vec3(0., height, 0.), .05, .005 ), 
        oscillatingSphere, 
        0.05
    );

    return min(d, dFire);
}

Material sceneMaterialCandles(vec3 position) 
{
