    vec4 qq = quat( 15. * position.y, vec3(0., 1., 0.));
    vec3 q  = mul(qq, position);

    radius *= 5. + 4. * (hash11(floatBitsToUint(15. + id)) * 2. - 1.);
    float d2 = sdBox(q, vec3(radius, height - .07, radius));
    d2 -= .02; // Smoothen corners

    float candle = opSmoothUnion(d1, d2, .1);
