    );
    if(dFire < d)
    {
        d = dFire;
        material.baseColor = rndColor(floatBitsToUint(id) + 10u);
        material.emission  = .5 * rndColor(floatBitsToUint(id) + 10u);
    }

    return material;
}

// Mixup geometry
bool isNeon(vec3 position) 
{
    const float repetitions = 30.;
    float stepSize = 2. * Pi / repetitions;
    float angle    = atan(position.z, position.x + 1e-15) + stepSize *.5;

    const float start = -7. * Pi;
    return sin((start + angle + max(iTime, 15.)) * .3) > 0.;
}

bool isBound(vec3 position) {
    const float repetitions = 30.;
    float stepSize = 2. * Pi / repetitions;
    float angle    = atan(position.z, position.x + 1e-15) + stepSize *.5;

    const float start = -7. * Pi;
    return abs(sin((start + angle + max(iTime, 15.)) * .3)) < 1e-2;
}

// General scene functions
LightSample sampleLight(vec3 position, vec4 u)
{
    if(isNeon(position))
        return sampleLightNeon(position, u);

    return sampleLightCandles(position, u);
}

float sdScene(vec3 position) 
{
    if(isNeon(position))
        return sdSceneNeon(position);
    return sdSceneCandles(position);
}

Material sceneMaterial(vec3 position) 
{    
    Material material;
    if(isNeon(position))
        material = sceneMaterialNeon(position);
    else 
        material = sceneMaterialCandles(position);

    if(isBound(position))
        material.emission = vec3(0.6, 1., 1.);

    return material;
}

// Map normal estimation
// by Inigo Quilez
// Reference: https://iquilezles.org/articles/normalsSDF
const float stepSize = 0.00001;
vec3 normal(vec3 pos)
{
    const vec2 e = vec2(1.0, -1.0) * 0.5773 * stepSize;
    return normalize( e.xyy*sdScene( pos + e.xyy ) + 
					  e.yyx*sdScene( pos + e.yyx ) + 
					  e.yxy*sdScene( pos + e.yxy ) + 
					  e.xxx*sdScene( pos + e.xxx ) );
}

// Sphere tracing
HitInfo trace(vec3 ro, vec3 rd, float tMax) 
{
    HitInfo hitInfo   = defaultHitInfo();
    bool  hit         = false;
    float t           = 0.;
    vec3  p           = vec3(0.);
    for(int i = 0; i < 150 && t < tMax; i++)
    {
        float d = sdScene(p = ro + rd * t);
        t      += max(abs(d), stepSize);

        // Hit offset depending on depth. Less precision when far away
        if(d < 2e-3 * t) 
        {
            hitInfo.t = t;
            break;
        }
    }

    if( hitInfo.t > 0. )
        hitInfo.normal = normal(p);        

    return hitInfo;
}

// Pathtracing integrator
vec3 render(vec2 fragCoord, int frame, inout HitInfo firstHit)
{
    vec4 alea = getBlueNoise(fragCoord, frame, iChannel2);

    vec3 ro, rd;
    getCamera(fragCoord, iResolution.xy, iMouse.xyz, frame, iTime, iChannel2, ro, rd);

    vec3 throughput = vec3(1.);
    vec3 finalColor = vec3(0.);
    firstHit.t = -1.;

    float bound = 15.;
    for(int b = 0; b < BounceNb; b++) 
    {
        HitInfo hit = trace(ro, rd, bound);
        if(hit.t < 0.) 
            break;

        if( b == 0 ) 
            firstHit = hit;

        vec3 position = ro + rd * hit.t;
        vec3 n        = hit.normal;
        n            *= sign(dot(n, -rd));

        Material material = sceneMaterial(position);
        finalColor += throughput * material.emission;


        vec3 pposition = offsetRay(position, hit.normal);
        vec4 transform = toLocalZ(hit.normal);
        hit.normal     = vec3(0., 0., 1.);

        vec3 woLocal   = normalize(mul(transform, -rd));
        vec3 direct    = vec3( 0. );

        // Direct lighting
        {
            LightSample lightSample = sampleLight(pposition, alea);

            vec3  wiLocal  = normalize(mul(transform, lightSample.wi));
            float cosTheta = wiLocal.z;
            if(lightSample.pdf > 0. && cosTheta > 0.) 
            {
                if(trace(pposition + lightSample.wi * 1e-1, lightSample.wi, length(pposition - lightSample.position)).t < 0.) 
                {
                    vec3 brdf = min(evalLambertian(material) * cosTheta, vec3(1.));
                    direct   += brdf * lightSample.intensity / max(1e-4, lightSample.pdf);
                }
            }            
        }
        finalColor += throughput * direct;

        vec3  weight        = evalLambertian( material );
        vec3  wiLocal       = sampleLambertian( woLocal, alea.wx );
        float scatteringPdf = getPdfLambertian( woLocal, wiLocal );

        float cosTheta = abs(woLocal.z);
        throughput    *= clamp(weight * cosTheta / max(1e-4, scatteringPdf), vec3(0.), vec3(1.));

        if(any(isinf(throughput)) || any(isnan(throughput)) )
            return vec3(0., 0., 0.);

        float luminance = getLuminance(throughput);
        if(luminance == 0. )
            return vec3(0.);

        rd   = normalize(mul(conjugate(transform), wiLocal));
        ro   = offsetRay(position, n);
        alea = getBlueNoise(fragCoord, frame, iChannel2);
    }

    return clamp(finalColor, vec3(0.), vec3(1.));
}

float sampleDistance(vec3 position, vec3 color, vec3 position2, vec3 color2) 
{
    vec3 colorDistance = rgb2lab(color) - rgb2lab(color2);
    return .001*length(position - position2) + .01*dot(colorDistance, colorDistance);
}

// #define DEBUG
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 uv = ivec2(fragCoord);

#ifdef DEBUG
    int frame = 0 * int(iFrame);

    vec3 ro, rd;
    getCamera(fragCoord, iResolution.xy, iMouse.xyz, frame, iTime, iChannel2, ro, rd);

    HitInfo hit = trace(ro, rd, 100.);

    fragColor.rgb = hit.normal * .5 + .5;
    fragColor.w   = 1.;
    return;
#else    
    vec4 data = vec4(0.);
    HitInfo hit = defaultHitInfo();
    for (float i = 0.; i < SampleNb; i++) 
        data.rgb += render(fragCoord, int(float(iFrame) * SampleNb + i), hit) / SampleNb;
    data.rgb = clamp(data.rgb, vec3(0.), vec3(1.));

    int currentFrame = int(float(iFrame) * SampleNb + SampleNb - 1.);

    vec3 ro, rd;
    getCamera(fragCoord, iResolution.xy, iMouse.xyz, currentFrame, iTime, iChannel2, ro, rd);

    vec3 position     = ro + rd * hit.t;
    Material material = sceneMaterial(position);

    vec2 mouse = vec2(
        texelFetch(iChannel0, ivec2(0, 1), 0).w,
        texelFetch(iChannel0, ivec2(0, 2), 0).w
    );
    int previousFrame = int(float(iFrame - 1) * SampleNb + SampleNb - 1.);
    vec2 fragCoord2   = getUV( position, iResolution.xy, iMouse.xyz, previousFrame, iTime, iChannel2 );

    float type       = (any(greaterThan(material.emission, vec3(0.))) ? -1. : 1.);
    bool wasRendered = all(lessThan(fragCoord2, iResolution.xy)) 
                    && all(greaterThan(fragCoord2, vec2(0.)));


    vec2 lastResolution  = texelFetch(iChannel0, ivec2(1), 0).rg;
    bool lastBufferValid = iFrame > 0 && all(equal(lastResolution, iResolution.xy));
    if(hit.t > 0. && wasRendered) 
    {
        // Reproject from best sample in neighboring pixels
        vec2  coordinates     = vec2(0.);
        float closestDistance = 1000000.;
        vec3  closestSample   = vec3(0.);

        vec3 minColor = vec3( 1000.);
        vec3 maxColor = vec3(-1000.);

        float radius = max(4., (ReprojectionRadius / 1280.) * iResolution.x);
        for(float x = -radius; x <= radius; x++)
        {
            for(float y = -radius; y <= radius; y++)
            {
                vec2 sampleUv = fragCoord2 + vec2(x, y);

                vec3 rro, rrd;
                getCamera(sampleUv, iResolution.xy, iMouse.xyz, previousFrame, iTime, iChannel2, rro, rrd);

                vec4  cSample      = texelFetch( iChannel1, ivec2(sampleUv), 0 );
                vec3  sampleColor  = cSample.rgb;
                float previousT    = cSample.w;
                float previousType = sign(cSample.w);
                previousT          = abs(previousT);
                vec3  previousP    = rro + rrd * previousT;

                float currentDistance = sampleDistance(position, data.rgb, previousP, sampleColor);
                if(currentDistance < closestDistance)
                {
                    coordinates     = sampleUv;
                    closestDistance = currentDistance; 
                    closestSample   = sampleColor;
                }

                minColor = min(minColor, sampleColor);
                maxColor = max(maxColor, sampleColor);
            }
        }

        // Do not reproject if closest sample is too far away
        //if(closestDistance < ReprojectionMaxDistance)
        {
            // Limit ghosting
            closestSample = clamp(closestSample, minColor, maxColor);

            // Moving average
            vec4  cSample     = texelFetch( iChannel1, ivec2(coordinates), 0 );
            vec3  sampleColor = clamp(cSample.rgb, minColor, maxColor);
            data.rgb = mix((closestSample*.7+sampleColor*.3), data.rgb, ReprojectionWeightMoving);
        }
    }

    // Tag emissive materials for denoise pass
    data.w = hit.t > 0. ? hit.t * type : 0.;
    fragColor = data;

    if(dot(fragCoord, fragCoord) < 1e-4)
        fragColor.rg = iResolution.xy;

#endif
}

