float weights[24] = float[](1.0 / 36.0, 1.0 / 9.0, 1.0 / 6.0, 1.0 / 9.0, 1.0 / 36.0, 1.0 / 9.0, 4.0 / 9.0, 2.0 / 3.0, 4.0 / 9.0, 1.0 / 9.0, 1.0 / 6.0, 2.0 / 3.0, 2.0 / 3.0, 1.0 / 6.0, 1.0 / 9.0, 4.0 / 9.0, 2.0 / 3.0, 4.0 / 9.0, 1.0 / 9.0, 1.0 / 36.0, 1.0 / 9.0, 1.0 / 6.0, 1.0 / 9.0, 1.0 / 36.0);

// Reprojection
vec3 getPosition(ivec2 fragCoord, float frameId, float depth) 
{
    vec2 mouse = vec2(
        texelFetch(iChannel0, ivec2(0, 1), 0).w,
        texelFetch(iChannel0, ivec2(0, 2), 0).w
    );

    vec3 ro, rd;
    getCamera(vec2(fragCoord), iResolution.xy, iMouse.xyz, int(frameId), iTime, iChannel1, ro, rd);

    return ro + rd * depth;
}

// Filtering
void evaluate(inout vec3 sum, inout float cumW, vec3 color, vec3 position, float type, float frameId, float stepWidth, vec2 fragCoord, vec2 offset, float kernelValue)
{
    vec2 uv = fragCoord + offset * stepWidth; 

    vec4  sampleData  = texelFetch(iChannel0, ivec2(uv), 0);
    vec3  sampleColor = sampleData.rgb;

    float sampleDepth = sampleData.w;
    float sampleType  = sign(sampleDepth);

    sampleDepth = abs(sampleDepth);

    // Weights from cornusammonis's shader
    // Reference: https://www.shadertoy.com/view/ldKBRV
    float cPhi = 0.5;
    float pPhi = 0.25;

    vec3 samplePosition = getPosition(ivec2(fragCoord), frameId, sampleDepth);
    vec3  positionDiff  = position - samplePosition;
    float positionDist  = dot(positionDiff, positionDiff);
    if(sampleDepth == 0. || sampleType != type)
        return;

    vec3  colorDiff = rgb2lab(color) - rgb2lab(sampleColor);
    float colorDist = dot(colorDiff, colorDiff);
    float wc        = min(exp(-colorDist / cPhi), 1.);

    float wp           = min(exp(-positionDist / pPhi ), 1.);
    float weight       = wc * wp; 

    sum  += sampleColor * weight * kernelValue; 
    cumW += weight * kernelValue;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    int   seed    = iFrame;
    float frameId = float(iFrame);
    vec4  buffer  = texelFetch(iChannel0, ivec2(fragCoord), 0 );
    float depth   = buffer.w;
    float type    = sign(depth);
    depth         = abs(depth);

    // Only filter pixels with an intersection
    if(depth > 0.)
    {
        vec3  sum  = vec3(0.0);
        float cumW = 0.0;

        vec3 position = getPosition(ivec2(fragCoord), frameId, depth);
        vec3 color    = buffer.rgb;

        // Random step width from: https://www.shadertoy.com/view/ldKBRV
        float size   = (2. / 1280.) * iResolution.x;
        float offset = (0. / 1280.) * iResolution.x;
        float stepWidth = getBlueNoise(vec2( fragCoord.x + 3. + iTime * 2., fragCoord.y * 4. + iTime), seed, iChannel1).x * size + offset;
        for(int i = 0; i < 24; i++)
        {
            vec2  offset = offsets[i];
            float weight = weights[i];
            evaluate(sum, cumW, color, position, type, frameId, stepWidth, fragCoord, offset, weight);
        }

        buffer.rgb = clamp(sum / cumW, vec3(0.), vec3(1.));
    }

    fragColor = buffer;
}

