// -- 2D Pass for first screen display
float draw(float x, float x0, float r) { return smoothstep( .01, .0,  abs(x-x0)-r/2.); }

float artDecoStraight(vec2 uv)
{
    const float Width = 4.;

    // Rhomb
    vec2 o = uv;
    uv.x = o.y + 2. * o.x; 
    uv.y = o.y - 2. * o.x; 

    // Repeat
    vec2 id = floor(uv * Width); 
    uv      = mod(uv * Width, 1.);

    // Angular repetition
    uv = uv * 2.;
    float Repetitions = 36.;
    float angle = Tau / Repetitions;

    float a  = atan(uv.y, uv.x) + angle/2.;
	float r  = length(uv);
	float c  = floor(a/angle);
	a        = mod(a,angle) - angle/2.;
	vec2 uv2 = vec2(cos(a), sin(a))*r;

    // The line is drawn for every section of the disk
    float d = 1. - draw(abs(a) - .05, .1, .2);
    return d * clamp(length(uv) - .5, 0., 2.);

}

#define rgb(c1, c2, c3) vec3(c1, c2, c3) / 255.
const vec3 MainColor      = rgb(251, 217, 130);
const vec3 SecondaryColor = rgb(195,  90, 76);
const vec3 ThirdColor     = rgb(255, 255, 190);

const float ReprojectionWeight = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.);

    int  seed            = int(iFrame);
    vec2 lastResolution  = texelFetch(iChannel0, ivec2(0), 0).xy;
    bool lastBufferValid = length(lastResolution - iResolution.xy) < 1e-1;
    vec4 previous        = texture(iChannel0, fragCoord / iResolution.xy);

    vec2 uv  = ((fragCoord + .5) / iResolution.y);
    vec2 uuv = ((fragCoord + .5) / iResolution.xy);
    vec4 alea      = getBlueNoise(vec2(fragCoord), seed, iChannel2);
    vec2 jittering = (alea.xy * 1.5 - 1.) / iResolution.xy;
    uv            += jittering;
    uuv           += jittering;

    // Top and bottom parts
    float slider = max(iTime * .5 - 3., 0.);
    if( abs( uuv.y - .5 ) - ( .18 + slider ) > 0.)
        fragColor.rgb = mix(SecondaryColor, ThirdColor, artDecoStraight(uv));

    // Centered lines 
    fragColor.rgb = mix(fragColor.rgb, SecondaryColor, float(abs(abs(uv.y - .5 ) - (.16 + slider) ) < 5e-3));
    fragColor.rgb = mix(fragColor.rgb, SecondaryColor, float(abs(abs(uv.y - .5 ) - (.18 + slider) ) < 5e-3));

    // Temporal accumulation + smooth apparition effect
    if(abs(uuv.y - .5) - (.15 + slider) > 0. && iTime > 0.1 && lastBufferValid)
        fragColor = mix(previous, fragColor, ReprojectionWeight);

    // Tag rendered pixels
    fragColor.w = float(dot(fragColor.rgb, fragColor.rgb) > 0.);

    // Text
    #define writeLetter( id ) writeChar( letter( id ), charUv, char ) 
    #define writeNumber( id ) writeChar( number( id ), charUv, char ) 

    DrawChar char = defaultDrawChar();
    float text1 = uv.x * uv.y * .5 + iTime * .5 - 1.;
    float text2 = uv.x * uv.y * .5 + iTime * .5 - 2.1;
    if(text1 < 1.)
    {
        // "JFIG 2023"
        vec2 charUv = ( uv - vec2(-.03, .345)) * 3.25; writeLetter(10); writeLetter(6); writeLetter(9); writeLetter(7); 
        charUv.x -= .5;                                writeNumber(2); writeNumber(0); writeNumber(2); writeNumber(3);
    }
    else if(text2 > 0. && text2 < 1.2)
    {
        // "AFIG 30 ANS"  
        vec2 charUv = (uv - vec2(.055, .42) ) * 4.; writeLetter(1); writeLetter(6); writeLetter(9); writeLetter(7);
        charUv      = (uv - vec2(.665, .32) ) * 4.; writeNumber(3); writeNumber(0); charUv.x-=.25;  writeLetter(1); writeLetter(14); writeLetter(19);
    }

    // Render text
    vec4 text = drawChar(char, iChannel1);
    if(text.x > 0.)
    {
        // Normalization of the text sdf from Marble Marcher: SE
        // Reference: https://www.shadertoy.com/view/3lKyDR
        float sdf = (text.w - .5 + 1. / 256.) * 75.;

        fragColor.rgb = MainColor;
        fragColor.w   = smoothstep(.1, .0, sdf );

        if(iTime > 0.1 && lastBufferValid) 
        {
            fragColor.rgb = mix(previous.rgb, fragColor.rgb, ReprojectionWeight);
            fragColor.w   = mix(previous.w,   fragColor.w,   ReprojectionWeight);
        }
    }

    slider = clamp(iTime * .25 - 36. / 4., 0., 1.);
    if( abs( uuv.y - .5 ) - (1. - slider) >= 0. ){
        fragColor.rgb = mix(SecondaryColor, ThirdColor, artDecoStraight(uv) * 1.5);
        fragColor.w   = 1.;

        if(iTime > 0.1 && lastBufferValid)
            fragColor = mix(previous, fragColor, ReprojectionWeight);
    }

    if(fragCoord.x < 1. && fragCoord.y < 1.)
        fragColor.rg = iResolution.xy;
}

