const float SMOOTHNESS = 0.5;
const vec3 BLOB_COL_BASE = vec3(1.0, 0.11, 0.8);
const vec3 BLOB_COL_GLOW = vec3(1.0, 0.9, 0.0);
const float ROWS = 6.0;

float smin(float a, float b, float k)
{
    float h = max(k-abs(a-b), 0.0)/k;
    return min(a, b) - h*h*k*(1.0/4.0);
}

float rand(int i, float lo, float hi) {
    return (hi - lo) * 0.5 * (sin(float(997*i)) + 1.) + lo;
}

vec4 perm(vec4 x) { x = ((x * 34.0) + 1.0) * x; return x - floor(x * (1.0 / 289.0)) * 289.0; }

float noise(vec3 p)
{
    vec3 a = floor(p);
    vec3 d = p - a;
    d = d * d * (3.0 - 2.0 * d);

    vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
    vec4 k1 = perm(b.xyxy);
    vec4 k2 = perm(k1.xyxy + b.zzww);

    vec4 c = k2 + a.zzzz;
    vec4 k3 = perm(c);
    vec4 k4 = perm(c + 1.0);

    vec4 o1 = fract(k3 * 0.02439024);
    vec4 o2 = fract(k4 * 0.02439024);

    vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
    vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);

    return o4.y * d.y + o4.x * (1.0 - d.y);
}

float rand1d(float n) { return fract(sin(n) * 43758.5453123); }

float noise1d(float p) 
{
	float fl = floor(p);
	float fc = fract(p);
	return mix(rand1d(fl), rand1d(fl + 1.0), fc);
}

float blob(vec2 uv, vec2 pos, float n, float radius, float period, int index) 
{ 
    float time = iTime * 0.3 + float(index) * 684.7291;
    int i = int(time / period);
    float t = mod(time, period) / period;

    pos.y = smoothstep(0., .4, t)*2.-1.;
    pos.y = mix(pos.y, -1., smoothstep(.5, .8, t)) * (ROWS - 1.0);

    vec2 p = uv - pos + n;
    return length(p) - radius;
}

float sdf(vec2 uv) 
{
    float d = 9999999.;
    float n = noise(vec3(uv, iTime * .2) * 0.7) * 0.7;
    for (float i = -ROWS; i <= ROWS; i += 1.0) 
    {
        float r = noise1d(i+iTime*0.2);
        d = smin(d, blob(uv, vec2(i * iResolution.x / iResolution.y * 0.8, 0.0), n, (0.7 + r*2.) * 0.8, 8.0 + abs(rand1d(i)) * 8.0, int(i)), SMOOTHNESS);
    }
    return d;
}

float specular(vec3 light_dir, vec3 normal) {
    light_dir = normalize(light_dir);
    vec3 view_dir = vec3(0,0,-1);
    vec3 halfway = normalize(light_dir + view_dir);
    float s = max(0.0, dot(normal, halfway));
    return s * s * s * s * s * s;
}

const uint MAX_RAY_MARCH_STEPS = 32u;
const float MAX_DISTANCE = 1.0;
const float SURFACE_DISTANCE = 0.01;
const float HALF_PI = 1.57079632679;

struct Ray
{
    vec3 pos;
    vec3 dir;
};

struct Camera
{
    Ray ray;
    float fov;
};
Camera camera;

struct Hit
{
    float dist;
    float min_dist;
    vec3 point;
    vec3 normal;
};

float specularBlinnPhong(vec3 light_dir, vec3 ray_dir, vec3 normal)
{
    return max(0.0, dot(normal, normalize(light_dir + ray_dir)));
}

float fbm(vec3 p)
{
    return (noise(p * 1.4) + noise(p * 2.25 + 16.791) * 0.6 + noise(p * 3.375 - 31.613) * .35) * 0.6;
}

float SDF(vec3 point, float seed)
{
    return (dot(point, point) - 0.16 - fbm(vec3(point.xy, iTime * 0.25 + point.z) + seed) * 0.25) * 0.7;
}

Hit raymarch(Ray ray, float seed)
{
    Hit hit;
    hit.dist = 0.0;
    hit.min_dist = MAX_DISTANCE;
    hit.normal = vec3(0);
    for (uint i = 0u; i < MAX_RAY_MARCH_STEPS; ++i)
    {
        hit.point = ray.pos + ray.dir * hit.dist; 
        float sdf = SDF(hit.point, seed);
        hit.min_dist = min(hit.min_dist, sdf);
        hit.dist += sdf;
        if (hit.dist >= MAX_DISTANCE)
            return hit;
        if (abs(sdf) <= SURFACE_DISTANCE)
        {
            vec2 e = vec2(0.002, 0.0);
            hit.normal = normalize(sdf - vec3(SDF(hit.point - e.xyy, seed), SDF(hit.point - e.yxy, seed), SDF(hit.point - e.yyx, seed)));
            return hit;
        }
    }

    return hit;
}

vec4 getColor(Ray ray, vec3 color, float seed)
{
    vec3 col = vec3(0);
    Hit hit = raymarch(ray, seed);
    if (hit.normal != vec3(0))
    {
        vec3 n = abs(hit.normal + vec3(0, 0, 0.5));
        col = normalize(color + n * n);
        col += pow(specularBlinnPhong(normalize(vec3(100, 100, -100) - hit.point), normalize(camera.ray.pos - hit.point), hit.normal), 32.0) * 0.5;
    }
    else
    {
        col = vec3(pow(max(0.0, 1.0 - hit.min_dist), 16.0)) * color;
        hit.dist = MAX_DISTANCE + 1.0 - max(0.0, 1.0 - hit.min_dist);
    }
    return vec4(col, hit.dist);
}

vec3 first(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;    
    camera.fov = 90.0;
    camera.ray.pos = vec3(0, 0, -1);
    camera.ray.dir = vec3(0, 0, 1);
    float fov = tan(HALF_PI - radians(camera.fov) * 0.5);
    camera.ray.dir = normalize(vec3(uv, fov));

    vec4 c1 = getColor(camera.ray, vec3(1, 0.35, 1), 0.0);
    vec4 c2 = getColor(camera.ray, vec3(0.1, 0.5, 1.0), 1000.612);

    if (c1.w > c2.w) { vec4 temp = c1; c1 = c2; c2 = temp; }

    if (c1.w >= MAX_DISTANCE)
    {
        fragColor = vec4(mix(c1.rgb, c2.rgb, 0.5), 1);
        return fragColor.rgb;
    }

    fragColor.rgb = c1.rgb;
    if (c2.w < MAX_DISTANCE) fragColor.rgb = mix(fragColor.rgb, c2.rgb, 0.5);
    fragColor.rgb = sqrt(fragColor.rgb * 1.1 - .1);
    return fragColor.rgb;
}

vec3 second(out vec4 fragColor, in vec2 fragCoord)
{
    float min_res = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min_res * ROWS;
    vec3 uvn = normalize(vec3(uv, 1.0));

    float d = sdf(uv); 
    vec2 e = vec2(8.0, 0);
    float nx = (sdf(uv - e.xy) - sdf(uv + e.xy)) / (2.0 * e.x);
    float ny = (sdf(uv - e.yx) - sdf(uv + e.yx)) / (2.0 * e.x);
    vec3 n = normalize(vec3(nx, ny, -1.));
    float m = smoothstep(0.0, 0.0 - fwidth(uv).y * 2., d);
    float s = noise(vec3(uv, iTime * .5 + 630.737551) * 1.0) * 0.5;
    float spec = max(0.0, uvn.y) * specular(vec3(uvn.x,-3.,0.0), n);
    spec += min(1.0, 1.-uvn.y) * specular(vec3(uvn.x,3.,0.0), n);
    spec = spec / (spec + 1.0) * 1.5;
    vec3 col = spec * spec * (BLOB_COL_GLOW * 0.3 + 0.7) + mix(BLOB_COL_BASE, BLOB_COL_GLOW, spec);
    col -= max(0.0, 1.- pow(abs(-d), 0.25)) * 0.7;
    fragColor = vec4(col * m + BLOB_COL_BASE * (1.0 - m) / (6.0 + d), 1.0);
    return fragColor.rgb;
}

const float OPACITY = 0.2;
const vec3 HALO_COL = vec3(0.2, 0.6, 1.0);
const vec3 EDGE1_COL = vec3(1.0, 0.68, 0.66);
const vec3 EDGE2_COL = vec3(1.0, 0.3, 0.2);
const vec3 BACKGROUND_COL = vec3(0, 0, 0.11);

vec2 rot(vec2 v, float a) 
{
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, s, -s, c);
	return m * v;
}

vec3 circle(vec2 uv, float off) 
{
    vec3 col = HALO_COL;
    float t = iTime * 0.5 + off;
    float rt = t * 0.4;
    float f = fwidth(uv).y * 4.0;
    uv = rot(uv, 6.2831853 * (noise1d(rt - 6816.6) * 0.5 + noise1d(rt * 1.25 - 3743.16) * 0.4 + noise1d(rt * 1.5 + 1741.516) * 0.3));
    float n = noise(vec3(uv * 1.2, t)) * 0.2 + noise(vec3(-uv * 1.7, t)) * 0.15 + noise(vec3(uv * 2.2, t)) * 0.1;
    float d = dot(uv, uv);
    float hd = d + n;
    col = pow(vec3(hd), vec3(3.5, 3.5, 2.0)) * HALO_COL * smoothstep(1.0, 1.0 - f, hd);
    float cd = d * hd * hd * smoothstep(1.0, 1.0 - f, hd) * 1.25;
    col += cd * cd * mix(EDGE1_COL, EDGE2_COL, pow(hd, 8.0)) - (cd * cd * cd) * col;
    col = mix(BACKGROUND_COL, col, smoothstep(1.0, 1.0 - f, hd));
    return col;
}

vec3 third( out vec4 fragColor, in vec2 fragCoord )
{
    float min_res = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min_res * 1.1;

    vec3 col = mix(mix(mix(mix(
    circle(uv, 0.0), 
    circle(uv, 1000.0), OPACITY), 
    circle(uv, 2000.0), OPACITY), 
    circle(uv, 3000.0), OPACITY), 
    circle(uv, 4000.0), OPACITY);
    return col;
}

#define MAX_RAY_MARCH_STEPS 32
#define MAX_DISTANCE 4.0
#define SURFACE_DISTANCE 0.002

struct Hit2
{
    float dist;
    float closest_dist;
    vec3 p;
};

float specularBlinnPhong2(vec3 light_dir, vec3 ray_dir, vec3 normal)
{
    vec3 halfway = normalize(light_dir + ray_dir);
    return max(0.0, dot(normal, halfway));
}

float SDF(vec3 point)
{
    vec3 p = vec3(point.xy, iTime * 0.3 + point.z);
    float n = (noise(p) + noise(p * 2.0) * 0.5 + noise(p * 4.0) * 0.25) * 0.57;
    return length(point) - 0.35 - n * 0.3;
}

vec3 getNormal(vec3 point)
{
    vec2 e = vec2(0.002, 0.0);
    return normalize(SDF(point) - vec3(SDF(point - e.xyy), SDF(point - e.yxy), SDF(point - e.yyx)));
}

Hit2 raymarch(vec3 p, vec3 d)
{
    Hit2 hit;
    hit.closest_dist = MAX_DISTANCE;
    for (int i = 0; i < MAX_RAY_MARCH_STEPS; ++i)
    {
        float sdf = SDF(p);
        p += d * sdf; 
        hit.closest_dist = min(hit.closest_dist, sdf);
        hit.dist += sdf;
        if (hit.dist >= MAX_DISTANCE || abs(sdf) <= SURFACE_DISTANCE)
            break; 
    }

    hit.p = p;
    return hit;
}

vec3 fourth(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    fragColor = vec4(0, 0, 0, 1);
    if (dot(uv, uv) > 1.0) return fragColor.rgb;
    vec3 pos = vec3(0, 0, -1);
    vec3 dir = normalize(vec3(uv, 1));

    Hit2 hit = raymarch(pos, dir);
    fragColor = vec4(pow(max(0.0, 1.0 - hit.closest_dist), 32.0) * (max(0.0, dot(uv, vec2(0.707))) * vec3(0.3, 0.65, 1.0) + max(0.0, dot(uv, vec2(-0.707))) * vec3(0.6, 0.35, 1.0) + vec3(0.4, 0.5, 1.0)), max(0.0, hit.closest_dist));
    if (hit.closest_dist >= SURFACE_DISTANCE)
        return fragColor.rgb;
    vec3 normal = getNormal(hit.p);

    vec3 ray_dir = normalize(pos - hit.p);
    float facing = max(0.0, sqrt(dot(normal, vec3(0.707, 0.707, 0))) * 1.5 - dot(normal, -dir));
    fragColor = mix(vec4(0), vec4(0.3, 0.65, 1.0, 1.0), 0.75 * facing * facing * facing);

    facing = max(0.0, sqrt(dot(normal, vec3(-0.707, -0.707, 0))) * 1.5 - dot(normal, -dir));
    fragColor = vec4(fragColor.rgb, 0) + mix(vec4(0), vec4(0.6, 0.35, 1.0, 1.0), 0.75 * facing * facing * facing);

    facing = max(0.0, sqrt(dot(normal, vec3(0.0, 0.0, -1.0))) * 1.5 - dot(normal, -dir));
    fragColor = vec4(fragColor.rgb, 0) + mix(vec4(0), vec4(0.4, 0.5, 1.0, 1.0), 0.5 * facing * facing * facing);

    fragColor = vec4(fragColor.rgb, 0) + mix(vec4(0), vec4(0.4, 0.625, 1.0, 1.0), pow(specularBlinnPhong(normalize(vec3(600, 800, -500) - hit.p), ray_dir, normal), 12.0) * 1.0);
    fragColor = vec4(fragColor.rgb, 0) + mix(vec4(0), vec4(0.6, 0.5625, 1.0, 1.0), pow(specularBlinnPhong(normalize(vec3(-600, -800, -00) - hit.p), ray_dir, normal), 16.0) * 0.75);
    fragColor.rgb = pow(fragColor.rgb, vec3(1.25));
    return fragColor.rgb;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    const float SMOOTH = 0.15;
    vec2 uv = fragCoord / iResolution.xy;
    vec3 a = first(fragColor, fragCoord * 2.0 - vec2(iResolution.x, 0));
    vec3 b = second(fragColor, fragCoord * 2.0);
    vec3 m1 = mix(a, b, smoothstep(0.5 + SMOOTH, 0.5 - SMOOTH, uv.x));

    vec3 c = third(fragColor, fragCoord * 2.0 - vec2(0, iResolution.y));
    vec3 d = fourth(fragColor, fragCoord * 2.0 - vec2(iResolution.x, iResolution.y));
    vec3 m2 = mix(d, c, smoothstep(0.5 + SMOOTH, 0.5 - SMOOTH, uv.x));

    fragColor.rgb = mix(m2, m1, smoothstep(0.5 + SMOOTH, 0.5 - SMOOTH, uv.y));
    fragColor.a = 1.0;
}

void mainImage( out vec4 O, vec2 U ){
    vec2 r = iResolution.xy;
    vec2 uv = U/r;

    O = texture(iChannel0, uv);

    float minD = 2.;
    float x = 0.;
    for (float i = 0.; i<r.x; i++){
        x = length(texture(iChannel0, texture(iChannel1, uv).xy/scale).xy - uv);
        if (x < minD){
            minD = x;
        }
    }

    O = vec4(1);
    O*= smoothstep(.0, 10./r.x, minD);

    O = texture(iChannel1, uv);
}

