// Voronoi buffer


void mainImage( out vec4 O, vec2 U ){
    vec2 r = iResolution.xy;
    vec2 uv = U/r;

    if (iFrame < 20){
        O = vec4(0);
        O.xy = uv;
        return;
    }


    O = texture(iChannel1, uv);
    // xy is data location of my particle

    vec2 myParticle = texture(iChannel0, O.xy/scale).xy;


    for (int i = 0; i < 8; i++){
        vec2 samp = mod(O.xy
            + (circleSample(uvec3(U, iDate.w*1000.))*2.-1.
            +  circleSample(uvec3(U, iDate.w*2000.))*2.-1.
            +  circleSample(uvec3(U, iDate.w*3000.))*2.-1.)*.001
        ,1.);

        vec2 newParticle = texture(iChannel0, samp/scale).xy;


        if (length(newParticle - uv) < length(myParticle - uv)){
            O = vec4(0);
            O.xy = samp;
        }
    }
}

