vec2 rotate(vec2 p, float angle) {
    float s = sin(angle);
    float c = cos(angle);
    mat2 rotationMatrix = mat2(c, -s, s, c);
    return rotationMatrix * p;
}

vec3 palette(float t) {
    // Create time-varying coefficients for a, b, c, and d
    vec3 a = vec3(0.488 * cos(t), -0.212 * sin(0.5*t), 0.528 * cos(2.0 * t));
    vec3 b = vec3(0.298 * sin(0.5 * t), 0.738 * cos(t), -0.112 * sin(t));
    vec3 c = vec3(-1.942 * cos(1.5 * t), -2.312 * sin(t), -3.142 * cos(0.3 * t));
    vec3 d = vec3(1.098 * sin(0.7 * t), -1.982 * cos(t), 0.667 * sin(2.0 * t));

    return a + b * cos(6.28318 * (c * t + d));
}



void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;

    // Apply a rotation to uv based on time (iTime)
    float rotationAngle = 0.1*iTime; // You can adjust the rotation speed as needed
    uv = rotate(uv, rotationAngle);

    vec3 finalcolor = vec3(0.0);

    for (float i = 0.0; i < 4.0; i++) {
        uv = uv *(2.2+ 1.4*cos(0.1*iTime));
        uv = fract(uv);
        uv = uv - 0.5;
        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + 0.4 * i + 0.4*iTime );

        d = sin(8.0 * d + iTime) / 8.0;
        d = abs(d);

        //d = pow(smoothstep(0.0, 0.1, d),1.8);
        d = pow(0.01 / d, 1.8);
        //d = pow(5.0*exp(-d), 1.8);

        finalcolor += col * d;
    }

    fragColor = vec4(finalcolor, 1.0);
}

// Type 2 Hexagonal Tilings
//
// There are 3 types of convex monohedral hexagonal tilings, see:
// https://en.wikipedia.org/wiki/Hexagonal_tiling#Monohedral_convex_hexagonal_tilings
// or eg. Grunbaum and Shepard for details, and the same constructions
// works for non-convex tiles as well (though there may be non-convex tilings
// that are not constructible in this way).
//
// This shader constructs a Type 2 tiling based on a rectangular
// repeating unit, and using two control points within the unit to
// construct the tiling (zooming in and using 'g' to see the underlying grid
// will probably be more useful than me trying to explain it). For
// some positions of the control points, the tile boundary intersects
// itself, or the tile is incompletely drawn (a limitation of doing this
// in a fragment shader).
//
// The resulting tiling consists of translations and rotations of the
// basic tile and its mirror image.
//
// Various subtypes of tiling are possible, eg. if one or more sides of the basic
// tile has length zero, or a corner angle is 180Â°, then the tiling is in fact
// of pentagons, quadrilaterals or triangles. Keys q,r,s show some possibilities.
//
// If the edges of the tile are replaced with curves with appropriate symmetry,
// then we have tiling satisfying a criterion similar to the 'Conway criterion'
// for Type 1 Tilings: https://en.wikipedia.org/wiki/Conway_criterion
//
// By varying the control point positions across the image we can do
// 'parquet deformations' - currently just a displacement to the two
// control points. Better ways are possible I'm sure.
//
// <mouse>: drag control points when not in animation mode
// <up/down>: zoom in & out
// a: animation
// c: colouring
// g: show grid
// p: show grid parity
// q,r,s: use special values for the control points
// x: don't show control points
// z: extra zoom out

// Point reflect p in q
vec2 pointreflect(vec2 p, vec2 q) {
  return 2.0*q - p;
}

// Vertical glide reflection
vec2 vglide(vec2 q, vec2 a) {
  return vec2(a.x-q.x,a.y+q.y);
}

int wind(vec2 p, vec2 q, vec2 r) {
  // NR winding number computation
  q -= p; r -= p; // Relative to p
  float t = determinant(mat2(q,r));
  if (q.y <= 0.0) {
    if (r.y > 0.0 && t > 0.0) return 1;
  } else {
    if (r.y <= 0.0 && t < 0.0) return -1;
  }
  return 0;
}

float vmin(vec2 p) {
  return min(p.x,p.y);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  setscale();
  vec2 p = map(fragCoord);
  float px = 1.4*2.0*scale/iResolution.y;
  vec2 a = getselection(0); // rectangle corner
  p /= a;
  ivec2 ix = ivec2(floor(p));
  p = mod(p+1.0,2.0)-1.0;
  p *= a;
  bool parity = p.x < 0.0 == p.y < 0.0;
  ivec2 inc = ivec2(1);
  if (p.y < 0.0) { 
    p = -p; inc = -inc;
  }
  if (p.x < 0.0) {
    p.x = a.x+p.x;
    p.y = a.y-p.y;  
    inc.y = -inc.y;
  }
  // q0,q,r,r0
  vec2 q = getselection(1);
  vec2 r = getselection(2);
  if (!key(CHAR_A)) {
    // Keep control points within two rectangle units. 
    float t = 0.5*iTime;
    q = mod(vec2(0.5,0.8)+0.618*t*vec2(1,0.618),2.0*a*vec2(1,2));
    q = min(q,2.0*a*vec2(1,2)-q);
    q.y -= a.y;
    r = mod(vec2(0.8,0.2)+t*vec2(1,0.618),2.0*a*vec2(1,2));
    r = min(r,2.0*a*vec2(1,2)-r);
    r.y -= a.y;
  }
  if (!key(CHAR_D)) {
    vec2 off = 0.1*(2.0*fragCoord-iResolution.xy)/iResolution.y;
    q += off;
    r += off.yx;
  }
  if (key(CHAR_Q)) q = vec2(0);
  if (key(CHAR_R)) r = vec2(a.x,0);
  if (key(CHAR_S)) r = q;
  vec2 q0 = pointreflect(q,vec2(0));
  vec2 r0 = pointreflect(r,vec2(a.x,0));
  vec2 Q0 = vglide(q0,a), Q = vglide(q,a);
  vec2 R = vglide(r,a), R0 = vglide(r0,a);
  vec2 R1 = r + vec2(0,2.0*a.y);
  vec2 q1 = Q - vec2(0,2.0*a.y);

  float d = 1e8;
  d = min(d,segment(p,q0,q));
  d = min(d,segment(p,q,r));
  d = min(d,segment(p,r,r0));
  d = min(d,segment(p,Q0,Q));
  d = min(d,segment(p,Q,R));
  d = min(d,segment(p,R,R0));
  d = min(d,segment(p,q,R));
  d = min(d,segment(p,Q,R1));
  d = min(d,segment(p,q1,r));

  // Next seven are just drawn as lines to fill in some
  // gaps, we don't use these segments as boundaries.
  d = min(d,segment(p,r0,Q0));
  d = min(d,segment(p,r0,q0+vec2(2.0*a.x,0)));
  d = min(d,segment(p,Q0,R0+vec2(2.0*a.x,0)));

  d = min(d,segment(p,q0,R0-vec2(0,2.0*a.y)));
  d = min(d,segment(p,q0,r0-vec2(2.0*a.x,0)));

  d = min(d,segment(p,R0,q0+vec2(0,2.0*a.y)));
  d = min(d,segment(p,R0,Q0-vec2(2.0*a.x,0)));

  // Check for which region p is in. There are
  // some redundant calls to wind() here (since
  // wind(p,q,r) = -wind(p,r,q), more or less).
  int k = wind(p,r0,r)+wind(p,r,q1);
  if (k > 0) ix += inc*ivec2(1,-1);
  k = wind(p,q1,r)+wind(p,r,q)+wind(p,q,q0);
  if (k > 0) ix.y -= inc.y;
  k = wind(p,q0,q)+wind(p,q,R)+wind(p,R,R0);
  if (k > 0) ix.x -= inc.x;
  k = wind(p,R0,R)+wind(p,R,Q)+wind(p,Q,R1);
  if (k > 0) ix.y += inc.y;
  k = wind(p,R1,Q)+wind(p,Q,Q0);
  if (k > 0) ix += inc;

  vec3 col = vec3(1,1,0.8);
  float h = rand(ix); // float(ix.x+ix.y)/32.0;
  if (!key(CHAR_C)) col = hsv2rgb(h,0.4,1.0);

  if (key(CHAR_P) && parity) col *= 0.9;
  col *= smoothstep(0.0,px,d-0.01);
  if (key(CHAR_G)) {
    vec2 tmp = min(p,a-p);
    col *= smoothstep(0.0,px,vmin(min(p,a-p)));
  }
  if (!key(CHAR_X)) {
    // Show the control points
    float d = 1e8;
    vec2 p = map(fragCoord);
    d = min(d,distance(p,a));
    d = min(d,distance(p,q));
    d = min(d,distance(p,r));
    col *= smoothstep(0.0,px,d-0.06);
  }
  col = pow(col,vec3(0.4545));
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1);
}

