// Public Domain under http://unlicense.org, see link for details.

// Consider the following problem: "what fraction of [-1;+1]x[-1;+1] square
// is covered by A*x+B*y+C>=0 halfplane?". Assuming, without loss of
// generality, that A>=B>=0, the exact solution is:
//     0                   , if C<=-(A+B)
//     (A+B+C)^2/(8*A*B)   , if -(A+B)<=C<=-(A-B)
//     0.5+0.5*C/max(A,B)  , if |C|<=(A-B)
//     1-(A+B-C)^2/(8*A*B) , if +(A-B)<=C<=+(A+B)
//     1                   , if C>=+(A+B)
// Note that coverage(A,B,C)=coverage(B,A,C)=coverage(-A,B,C)=coverage(A,-B,C)=1-coverage(A,B,-C).
//
// You can use this to approximate anti-aliasing for some SDF-like function.
// If you know function f() and its derivatives f'x() and f'y() (or can
// approximate them), then the part of a rectangle ("pixel") [x-w/2;x+w/2]*[y-h/2;y+h/2],
// covered by f(x)<0 can be approximated as the above problem with
//     A=w*f'x(x,y)
//     B=h*f'y(x,y)
//     C=-2*f(x,y)
//
// Of course, you are not *required* to do the anti-aliasing this way. E.g using
// gaussian (i.e. erf) has its appeal (despite infinite support).
//
// Accuracy of some approximations (see https://godbolt.org/z/z38xPesd3):
//   Linf     - infiniy norm (i.e. max. abs. error)
//   RMS      - root mean square error
//   Inside   - max. error when wrongly returning {0,1}
//   Outside  - max. error when wrongly returning (0;1)
// Note: A, B, C are uniformly distributed in [-1;+1]x[-1;+1]x[-sqrt(2);+sqrt(2)].
// This affects computed RMS.
// | Color     | Function            | Linf        | RMS         | Inside      | Outside     |
// |-----------|---------------------|-------------|-------------|-------------|-------------|
// | Gray      |cov_exact            |  0.00000000 |  0.00000000 |  0.00000000 |  0.00000000 |
// |           |cov_msaa< 1>         |  0.49999636 |  0.20408611 |  0.49999636 |  0.00000000 |
// | Violet    |cov_msaa< 2>         |  0.24952590 |  0.07040552 |  0.24723244 |  0.00000000 |
// |           |cov_msaa< 4>         |  0.12452590 |  0.02481787 |  0.12280673 |  0.00000000 |
// |           |cov_msaa< 8>         |  0.06172752 |  0.00875571 |  0.06071138 |  0.00000000 |
// |           |cov_msaa<16>         |  0.03047752 |  0.00309934 |  0.02749920 |  0.00000000 |
// |           |cov_msaa<32>         |  0.01511553 |  0.00110750 |  0.01278222 |  0.00000000 |
// |           |cov_msaa<64>         |  0.00719970 |  0.00038004 |  0.00280529 |  0.00000000 |
// | Cyan      |cov_step             |  0.49999636 |  0.20408611 |  0.49999636 |  0.00000000 |
// | Yellow    |cov_lin              |  0.12499946 |  0.06321841 |  0.00000000 |  0.00000000 |
// | Pink      |cov_smooth           |  0.09602143 |  0.02111146 |  0.00000000 |  0.00000000 |
// | Blue      |cov_mix              |  0.03703614 |  0.01561240 |  0.00000000 |  0.00000000 |
// | Green     |cov_sqrt             |  0.04289210 |  0.01701449 |  0.04265356 |  0.00000000 |
// | Magenta   |cov_smoothsqrt       |  0.03111339 |  0.00881568 |  0.00494635 |  0.03111339 |
// | Orange    |cov_erf              |  0.04798281 |  0.01579871 |  0.00000000 |  0.04736865 |
// |           |cov_tanh             |  0.05639169 |  0.02119081 |  0.00000000 |  0.05517477 |
// |           |cov_atan             |  0.09770226 |  0.05461564 |  0.00000000 |  0.09770226 |
// | L. blue   |cov_si               |  0.08948976 |  0.04159512 |  0.00000000 |  0.08948976 |

//==============================================================================
// Some constants and special functions.

#define pi 3.14159265358979

// From Abramowitz and Stegun. Max. abs. error ~5e-4,
// which is somewhat better than https://www.shadertoy.com/view/7dXXDN
// which has max. abs. error ~3.6e-3 using BÃ¼rmann series.
float erf(float x)
{
    float y=abs(x),t=1.0+y*(0.278393+y*(0.230389+y*(0.000972+y*0.078108)));
    return sign(x)*(1.0-1.0/((t*t)*(t*t)));
}

// From Abramowitz & Stegun.
// Max. abs. error ~1e-6.
float Si(float x)
{
    // Adapted from https://github.com/GalSim-developers/GalSim/blob/releases/2.5/src/math/Sinc.cpp
    // NOTE: this is based on #ifdef'd out version.
    float x2=x*x;
    if(x2<1.0) return x*(1.0+x2*(-0.0555555556+x2*(0.00166666667+x2*-2.83446712e-05)));
    return ((pi/2.0)*((x>0.0)?1.0:-1.0)
        - (38.102495+x2*(335.677320+x2*(265.187033+x2*(38.027264+x2))))
        / (x*(157.105423+x2*(570.236280+x2*(322.624911+x2*(40.021433+x2)))))*cos(x)
        - (21.821899+x2*(352.018498+x2*(302.757865+x2*(42.242855+x2))))
        / (x2*(449.690326+x2*(1114.978885+x2*(482.485984+x2*(48.196927+x2)))))*sin(x));
}

//==============================================================================
// Coverage functions.

float cov_exact(float A,float B,float C)
{
    A=abs(A);B=abs(B);
    float s=A+B,d=abs(A-B),m=abs(C);
    if     (m<=d) return 0.5+0.5*C/max(A,B);
    else if(m>=s) return step(0.0,C);
    else          return abs(step(0.0,C)-(s-m)*(s-m)/(8.0*A*B));
}

#define MSAA 2
float cov_msaa(float A,float B,float C)
{
    float ret=0.0;
    for(int i=0;i<MSAA;++i) for(int j=0;j<MSAA;++j)
    {
        float x=2.0*(float(i)+0.5)/float(MSAA)-1.0;
        float y=2.0*(float(j)+0.5)/float(MSAA)-1.0;
        ret+=step(0.0,A*x+B*y+C);
    }
    return ret/float(MSAA*MSAA);
}

float cov_step(float A,float B,float C)
{
    return step(0.0,C);
}

float cov_lin(float A,float B,float C)
{
    return clamp(0.5*(1.0+C/(abs(A)+abs(B))),0.0,1.0);
}

float cov_smooth(float A,float B,float C)
{
    return smoothstep(-1.0,1.0,C/(abs(A)+abs(B)));
}

// This is mix(smooth,lin,factor).
float cov_mix(float A,float B,float C)
{
#if 0
    // 2-division version.
    // NOTE: we would need an extra abs, if we were raising
    // m to an odd power. Powers 2 (which we use), 3, and 4
    // give about equal accuracy.
    float s=abs(A)+abs(B),t=clamp(C/s,-1.0,+1.0),m=(abs(A)-abs(B))/s;
    t*=1.0+0.5*(1.0-t*t)*(1.0-m*m);
    return 0.5+0.5*t;
#else
    // 1-division version.
    // May overflow/underflow for |A|,|B| outside of [1e-6,1e+6].
    // May be slightly more accurate near |C|=|A|+|B|.
    float s=abs(A)+abs(B),s2=s*s,s4=s2*s2,c=clamp(C,-s,+s);
    return (s+c)*(0.5*s4+c*(s-c)*abs(A*B))/(s4*s);
#endif
}

float cov_sqrt(float A,float B,float C)
{
    return clamp(0.5*(1.0+C/sqrt(A*A+B*B)),0.0,1.0);
}

float cov_smoothsqrt(float A,float B,float C)
{
    // NOTE: the factor is (probably) 2/pi in terms
    // of pixel distance, but since we work in
    // terms of on 2x2 square ([-1;+1]x[-1;+1]),
    // it is doubled.
    float d=4.0/pi;
    return smoothstep(-d,+d,C/sqrt(A*A+B*B));
}

// NOTE: tanh and atan are similar to erf,
// but have worse max. error.
float cov_erf(float A,float B,float C)
{
#if 1
    // Constant numerically tuned to minimize Linf error.
    return 0.5+0.5*erf(1.1765*C/sqrt(A*A+B*B));
#else
    // Linf error is ~7.8% vs ~4.8% for the above version.
    // NOTE: 0.5+0.5*tanh(sqrt(2.0)*C/sqrt(A*A+B*B)) is ~5.7%.
    return 0.5+0.5*erf(C/sqrt(A*A+B*B));
#endif
}

float cov_si(float A,float B,float C)
{
    // NOTE: We work in terms of on 2x2 square ([-1;+1]x[-1;+1]),
    // so the radius is doubled.
    float R=2.0;
    float d=C/sqrt(A*A+B*B);
    return 0.5+Si(pi*d/R)/pi;
}

//==============================================================================
// Dispatcher

float coverage(float A,float B,float C,int id)
{
    switch(id)
    {
        case 0: return cov_exact     (A,B,C);
        case 1: return cov_msaa      (A,B,C);
        case 2: return cov_step      (A,B,C);
        case 3: return cov_lin       (A,B,C);
        case 4: return cov_smooth    (A,B,C);
        case 5: return cov_mix       (A,B,C);
        case 6: return cov_sqrt      (A,B,C);
        case 7: return cov_erf       (A,B,C);
        case 8: return cov_smoothsqrt(A,B,C);
        case 9: return cov_si        (A,B,C);
#define NUM_CASES 10 // Need to update when adding new functions!
    }
    return 0.0;
}

//==============================================================================
// Helper functions

vec3 linear2srgb(vec3 rgb)
{
    return mix(12.92*rgb,1.055*pow(rgb,vec3(1.0/2.4))-0.055,step(0.0031308,rgb));
}

vec3 palette(int id)
{
    return id==0?vec3(0.5):0.5+0.5*cos(8.0*float(id)+vec3(0.0,2.0,4.0));
}

//==============================================================================
// Main program

// Returns function in z, and derivatives in xy.
vec3 f(vec2 d)
{
    float l=length(d);
    float D=2.0,R=16.0;
    return vec3(
        2.0*d-2.0*R*normalize(d),
        (l-(R-0.5*D))*(l-(R+0.5*D)));
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    float W=iResolution.x;
    float H=iResolution.y;
    float M=max(W-H,0.25*H);
    float R=10.0;
    float G=1.0/32.0;
    float S=8.0;
    float D=(W-M)/float(NUM_CASES);
    int id=int(abs(iMouse.x)/D)%NUM_CASES;
    vec2 uv=fragCoord.xy/H;
    vec2 xy=(floor(H*(2.0*uv-1.0)/(2.0*S))+0.5);
    vec2 c=R*vec2(cos(0.73*iTime),sin(0.37*iTime));
    vec2 d=xy-c;
    vec3 F=f(d);
    float A=F.x,B=F.y,C=-2.0*F.z;
    float a=coverage(A,B,C,id);
    vec3 color=vec3(a);
    color.g+=G*step(1.0,mod(xy.x+xy.y,2.0))*exp(-16.0*(S*S/(H*H))*dot(xy,xy));
    if(uv.x*H<W-M&&uv.y*H<D)
    {
        float x=uv.x/((W-M)/H),y=uv.y/(D/H);
        x*=float(NUM_CASES);
        int idx=int(x);
        x=fract(x);
        vec2 d=D*(2.0*vec2(x,y)-1.0)-c;
        vec3 F=f(d);
        float A=F.x,B=F.y,C=-2.0*F.z;
        float a=coverage(A,B,C,idx);
        color=mix(color,palette(idx),a);
        if(id==idx) color=mix(color,palette(idx),exp(-0.25*D*D*x*(1.0-x)*y*(1.0-y)));
    }
    if(uv.x*H>W-M)
    {
        float angle=(3.14159265358979/4.0)*(floor(10.0*uv.y)/10.0);
        float A=cos(angle),B=sin(angle);
        float e=0.1;
        float x=(H*uv.x-(W-M))/M,y=fract(10.0*uv.y)*(1.0+2.0*e)-e;
        float h=0.1*H/M;
        x=(1.0+h)*x-h;
        if(x>=0.0)
        {
            float v0=cov_exact(A,B,sqrt(2.0)*(2.0*x-1.0))-y;
            float v1=coverage(A,B,sqrt(2.0)*(2.0*x-1.0),id)-y;
            float v=0.5+5.0*(v1-v0)-y;
            if(y>=0.0&&y<=1.0) color=mix(color,vec3(0.125),0.75);
            if(sign(iMouse.z)<0.0)
                color+=exp(-1e-2*H*H*v1*v1)*palette(id)+exp(-1e-2*H*H*v0*v0)*0.5;
            else
                color+=exp(-1e-2*H*H*v*v)*palette(id);
        }
        else
        {
            x=x/h+1.0;
            x=(2.0*x-1.0)/(1.0+e);
            y=2.0*y-1.0;
            if(abs(x)<1.0&&abs(y)<1.0)
            {
                color=mix(color,vec3(0.25+0.5*step(A*x+B*y,0.0)),0.75*exp(-2.0*(x*x+y*y)));
            }
        }
    }
    color=linear2srgb(color);
    fragColor=vec4(color,1.0);
}

// Fork of "3D Water Box" by michael0884. https://shadertoy.com/view/dscfRf
// 2023-10-16 20:53:25

#define SHADOWS 
#define REFRACT
#define REFLECT

const vec3 light = 2.0*vec3(1.0,1.0,1.0);
const vec3 absorb = vec3(0.584,0.843,0.953);
const vec3 albedo = vec3(0.000,0.000,0.000);
const vec3 F0 = vec3(0.05);
const float roughness = 0.065;

#define RADIUS 0.8
#define NORMAL_SMOOTHNESS 0.72

#define FOV 3.
mat3 getCamera(vec2 angles)
{
   mat3 theta_rot = mat3(1,   0,              0,
                          0,  cos(angles.y),  -sin(angles.y),
                          0,  sin(angles.y),  cos(angles.y)); 

   mat3 phi_rot = mat3(cos(angles.x),   sin(angles.x), 0.,
        		       -sin(angles.x),   cos(angles.x), 0.,
        		        0.,              0.,            1.); 

   return theta_rot*phi_rot;
}

vec3 getRay(vec2 angles, vec2 pos)
{
    mat3 camera = getCamera(angles);
    return normalize(transpose(camera)*vec3(FOV*pos.x, 1., FOV*pos.y));
}

vec3 qrot(vec3 x, vec4 q)
{
    return x + 2.0 * cross(cross(x, q.xyz) + q.w * x, q.xyz);
}


#define MAX_DIST 1e10

struct Ray 
{
    vec3 ro;
    vec3 rd;
    float td;
    vec3 normal;
    vec3 color;
};

vec4 conj_q(vec4 q)
{
    return vec4(-q.xyz, q.w);
}

void iEllipsoid(inout Ray ray, in vec3 p, in vec3 r, in vec4 q)
{
    vec3 ro = ray.ro - p;
    ro = qrot(ro, conj_q(q));
    vec3 rd = qrot(ray.rd, conj_q(q));

    vec3 r2 = r*r;
    float a = dot( rd, rd/r2 );
	float b = dot( ro, rd/r2 );
	float c = dot( ro, ro/r2 );
	float h = b*b - a*(c-1.0);
	if( h<0.0 ) return;

	float t = (-b - sqrt( h ))/a;
    if(t >= ray.td || t < 0.0) return;
    ray.normal = qrot(normalize( (ro + t * rd)/r2 ), q);
    ray.color = vec3(1.);
    ray.td = t;
}


vec2 iBox( in vec3 ro, in vec3 rd, in vec3 boxSize ) 
{
    vec3 m = sign(rd)/max(abs(rd), 1e-8);
    vec3 n = m*ro;
    vec3 k = abs(m)*boxSize;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

	float tN = max( max( t1.x, t1.y ), t1.z );
	float tF = min( min( t2.x, t2.y ), t2.z );

    if (tN > tF || tF <= 0.) {
        return vec2(MAX_DIST);
    } else {
        return vec2(tN, tF);
    }
}


void TraceCell(inout Ray ray, vec3 p)
{
    //load the particles 
    vec4 packed = LOAD3D(ch0, p);
    Particle p0, p1;
    unpackParticles(packed, p, p0, p1);

    if(p0.mass + p1.mass == 0u) return;
    Covariance c0, c1;
    unpackCovariance(LOAD3D(ch2, p), c0, c1);

    if(p0.mass > 0u) iEllipsoid(ray, p0.pos, RADIUS*c0.s, c0.q);
    if(p1.mass > 0u) iEllipsoid(ray, p1.pos, RADIUS*c1.s, c1.q);
}

void TraceCells(inout Ray ray, vec3 p)
{
    vec3 p0 = floor(p);
    vec4 rho = voxel(ch1, p);
    if(rho.z < 1e-3) return;
    range(i, -1, 1) range(j, -1, 1) range(k, -1, 1)
    {
        vec3 p1 = p0 + vec3(i, j, k);
        TraceCell(ray, p1);
    }
}


float Density(vec3 p)
{
    return trilinear(ch1, p).z;
}


vec4 calcNormal(vec3 p, float dx) {
	const vec3 k = vec3(1,-1,0);
	return   (k.xyyx*Density(p + k.xyy*dx) +
			 k.yyxx*Density(p + k.yyx*dx) +
			 k.yxyx*Density(p + k.yxy*dx) +
			 k.xxxx*Density(p + k.xxx*dx))/vec4(4.*dx,4.*dx,4.*dx,4.);
}



#define ISO_VALUE 0.2
#define STEP_SIZE 1.0
#define IOR 1.333

vec3 refractFull(vec3 rd, vec3 n, float ior)
{
    vec3 refr = refract(rd, n, ior);
    if(length(refr) < 0.5)
    {
        return reflect(rd, n);
    }
    else return refr;
}

float TraceDensity(vec3 ro, vec3 rd)
{
    float dens = 0.0;
    float td = 0.0;
    for(int i = 0; i < 40; i++)
    {
        vec3 p = ro + rd * td;
        if(any(lessThan(p, vec3(1.0))) || any(greaterThan(p, size3d - 1.0))) return dens;
        float d = Density(p);
        dens += d * 4.0;
        td += 4.0;
    }
    return dens;
}


float TraceDensityMedium(vec3 ro, inout vec3 rd)
{
    float dens = 0.0;
    float td = 0.0;
    float de = 0.0;
    float pde = 0.0;
    bool bounced = false;
    for(int i = 0; i < 60; i++)
    {
        vec3 p = ro + rd * td;
        if(any(lessThan(p, vec3(0.0))) || any(greaterThan(p, size3d))) return dens;
        float ldens = Density(p);
        float d = smoothstep(ISO_VALUE*0.9, ISO_VALUE*1.1, ldens);
        dens += d * STEP_SIZE;
        pde = de;
        de = ldens - ISO_VALUE;
        if(pde > 0.0 && de < 0.0 && !bounced && td > 4.0)
        {
            float std = td - STEP_SIZE*(de/(de-pde));
            vec3 sp = ro + rd*std;
            vec3 normal = normalize(calcNormal(sp, 0.5).xyz);
            ro = sp;
            rd = refractFull(rd, normal, IOR);
            td = 0.0;
            bounced = true;
        }
        td += STEP_SIZE;
    }
    return dens;
}


vec3 Background(vec3 rd)
{
    vec3 col = texture(iChannel3,  rd.yzx).xyz;
    return 2.0*pow(col, vec3(2.0)) + col*exp(15.0*(length(col) - 1.45));
}

vec3 fresnel(vec3 V, vec3 H, vec3 F0)
{
    return F0 + (1. - F0)*pow(1.0 - max(dot(V,H), 0.0), 5.0);
}

float NDF_ggx(vec3 m, vec3 n, float alpha)
{
    float alpha2 = alpha*alpha; 
    return alpha2/(PI*sqr( sqr(max(dot(n,m), 0.)) * (alpha2 - 1.0) + 1.0 ));
}

float G_ggx(float NdotV, float alpha)
{
    float alpha2 = alpha*alpha;
    return 2.0*NdotV/(NdotV + sqrt( mix(NdotV*NdotV, 1.0, alpha2) ));
}


vec3 PBR(vec3 P, vec3 V, vec3 L, vec3 Lcol, vec3 N, vec3 color, vec3 absorb)
{
    vec3 Re = reflect(-V, N);
    vec3 Rf = refract(-V, N, 1.0/1.33);
    vec3 H = normalize(V + L);
    float NdotL = max(dot(N, L), 2e-3);
    float NdotV = max(dot(N, V), 2e-3);

    #ifdef SHADOWS
    float dens = TraceDensity(P+L*2.0, L);
    float shadow = exp(-dens);
    float ambient = 0.4*exp(-0.3*dens) + 0.1*exp(-0.1*dens) + 0.05*exp(-0.05*dens);
    #else
    float shadow = 1.0;
    float ambient = 0.3;
    #endif

    #ifdef REFRACT
    float refrDens = TraceDensityMedium(P, Rf);
    vec3 refraction = Background(Rf) * exp(-0.5*refrDens*(1.0 - absorb));
    #else
    vec3 refraction = vec3(0.0);
    #endif

    #ifdef REFLECT
    float reflDens = TraceDensity(P, Re);
    vec3 reflection = Background(Re) * exp(-0.5*reflDens*(1.0 - absorb));
    #else
    vec3 reflection = (1.0 - roughness)*Background(Re);
    #endif

    float selfshadow = G_ggx(NdotL,roughness)*G_ggx(NdotV,roughness)/max(4.0*NdotL*NdotV,1e-3);
    float specular = selfshadow*NDF_ggx(H, N, roughness) * NdotL;

    vec3 Lbright = shadow * Lcol;

    vec3 refr = Lbright * color * NdotL / PI + refraction;
    vec3 relf = Lbright * specular + reflection;
    vec3 ambi = ambient * color * (0.7*NdotL + 0.3);

    vec3 kS = fresnel(V, N, F0);
    vec3 kD = 1.0 - kS;
    return (ambi + refr) * kD + relf * kS;
}


void mainImage( out vec4 col, in vec2 fragCoord )
{    
    InitGrid(iResolution.xy);

    vec2 uv = (fragCoord - 0.5*R)/max(R.x, R.y);

    vec2 angles = vec2(2.*PI, PI)*(iMouse.xy/iResolution.xy - 0.5);

    if(iMouse.z <= 0.)
    {
        angles = vec2(0.04 + 0.05*iTime, -0.5);
    }
    vec3 rd = getRay(angles, uv);
    vec3 center_rd = getRay(angles, vec2(0.));

    float d = sqrt(dot(vec3(size3d), vec3(size3d)))*0.4;
    vec3 ro = vec3(size3d)*vec3(0.5, 0.5, 0.5) - center_rd*d;

    vec2 tdBox = iBox(ro - vec3(size3d)*0.5, rd, 0.5*vec3(size3d));
    col.xyz = Background(rd);
    if(tdBox.x < MAX_DIST)
    {
        float td = max(tdBox.x+0.5, 0.0);
        Ray ray;
        ray.ro = ro;
        ray.rd = rd;
        ray.td = tdBox.y;
        int i = 0;
        bool stop = false;
        for(; i < 200; i++)
        {
            vec3 p = ro + rd*td;
            TraceCells(ray, p);
            td += 1.5;
            if(td > tdBox.y-1.0)
            {
                break;
            }
            if(stop) break;
            if(ray.td < tdBox.y-1.0)
            {
                stop = true;
            }
        }

        if(ray.td < tdBox.y)
        {
            vec3 p0 = ray.ro + ray.rd*ray.td;
            vec3 normal1 = normalize(calcNormal(p0, 0.5).xyz);
            vec3 normal =  -normalize(mix(-ray.normal, normal1, NORMAL_SMOOTHNESS));

            col.xyz = PBR(p0, -ray.rd, light_dir, light, normal, albedo, absorb);
        }
    }

    col.xyz = 1.0 - exp(-2.5*pow(col.xyz,vec3(1.0/1.3)));
}

