void AddDensity(inout Particle p, in Particle incoming, float rad)
{
    if(incoming.mass == 0u) return;
    float d = distance(p.pos, incoming.pos);
    float mass = float(incoming.mass);
    p.density += mass*GD(d,rad);
}

