float sawtooth_wave(float x, float freq, float amp) {
    return (x * freq - floor(x * freq)) * amp;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Hint, it's a few lines of code, and makes use of a tan function :)
    vec2 warpedUv = uv;

    float stripeBottom = 5.f + sawtooth_wave(iTime, 0.1, iResolution.y);
    float stripeTop = 80.f + sawtooth_wave(iTime, 0.1, iResolution.y);

    float dist = abs(fragCoord.y - stripeTop);

    if (fragCoord.y > stripeBottom && fragCoord.y < stripeTop) {
        warpedUv += 0.01 * dist * tan(fragCoord.y);
    }

    vec2 offset1 = vec2(0.03);
    vec2 offset2 = vec2(0.015);
    vec2 offset3 = vec2(0.0);

    float r = texture(iChannel0, warpedUv + offset1).r;
    float g = texture(iChannel0, warpedUv + offset2).g;
    float b = texture(iChannel0, warpedUv + offset3).b;

    vec3 col = vec3(r, g, b);

    // Output to screen
    fragColor = vec4(col,1.0);
}

