/*
Based on domrally's Squoval project.
    https://domrally.github.io/squoval
Asymptotically smooth & periodically flat functions.
    https://en.wikipedia.org/wiki/Smoothness
    https://en.wikipedia.org/wiki/Flat_function
*/
void mainImage(out vec4 o, vec2 i)
{
    vec2 R = iResolution.xy;
    i += i - R;
    float r = min(R.x, R.y);
    float k = 4. * (.5 + .5 * cos(iTime));
    k *= k;

    // Inverse error function method by Sergei Winitzki using PadÃ© approximations
    vec2 x = max(vec2(.01), (1. + k) * 1.2 * abs(i) / r - k),
         c = log(1. - x * x),
         b = 4.33 + c / 2.,
         a = sqrt(b * b - c / .147),
         z = tanh(sign(x) * sqrt(a - b));
    z *= z;
    float d = 1. - z.x - z.y;

    o = vec4(smoothstep(0., (25. + k * k) / r, d));
}


/*
REFERENCES:

Rounded Squares.
https://en.wikipedia.org/wiki/Squircle
https://en.wiktionary.org/wiki/roundrect
https://en.wikipedia.org/wiki/Superellipse

Smoothing.
https://en.wikipedia.org/wiki/Mollifier
https://en.wikipedia.org/wiki/Smoothness
https://en.wikipedia.org/wiki/Flat_function
https://en.wikipedia.org/wiki/Bump_function
https://en.wikipedia.org/wiki/Non-analytic_smooth_function
https://en.wikipedia.org/wiki/Smoothness#Smooth_partitions_of_unity
https://en.wikipedia.org/wiki/Error_function#Approximation_with_elementary_functions
*/

vec3 palette(float t) {
    float sinTime = sin(t);
    float cosTime = cos(t);
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.269, 0.417, 0.557);

    // Earthy gradient colors
    vec3 earthyColor1 = vec3(0.584, 0.376, 0.184); // Brown
    vec3 earthyColor2 = vec3(0.678, 0.631, 0.318); // Khaki
    vec3 earthyColor3 = vec3(0.251, 0.349, 0.156); // Olive Green

    float gradientFactor = 0.5 + 0.5 * sinTime;

    vec3 colorShift = vec3(sinTime, cosTime, sinTime * cosTime);
    return mix(
        a - b * cos(6.28318 * (c * t + d)) + colorShift,
        mix(earthyColor1, mix(earthyColor2, earthyColor3, gradientFactor), gradientFactor),
        0.5
    );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / max((iResolution.x * sin(2.0) - iTime) + cos(iTime / 9.0), iResolution.y + cos(iTime / 9.0));
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    float rotationSpeed = 0.0; // Adjust the rotation speed
    float rotationAngle = -sin(iTime * rotationSpeed); // Smooth rotation angle

    float aspect = iResolution.x / iResolution.y;
    float timeFactor = iTime / 5.0; // Adjust the time factor for the evolution

    float xBlend = fragCoord.x / iResolution.x; // Blending factor based on x-coordinate
    float yBlend = 1.0 - xBlend; // Blending factor for the opposite direction

    for (float i = 0.0; i < 4.0; i++) {  // Iterate four times for quad-mirroring
        // Apply rotation
        uv = mat2(cos(rotationAngle), -sin(rotationAngle), -sin(rotationAngle), tan(rotationAngle)) * uv;

        // Deform the UV coordinates over time from a circle to a rectangle
        uv.x = mix(uv.x, sign(uv.x) * (2.0 - abs(sin(timeFactor + uv.x))), abs(cos(timeFactor + uv.y) * 0.5));
        uv.y = mix(uv.y, sign(uv.y) * (2.0 - abs(cos(timeFactor + uv.y))), abs(sin(timeFactor + uv.x) * 0.5));

        uv = fract(uv * 2.5) - 0.5;

        float d = length(uv) * exp(+length(uv0));

        vec3 col = pow(palette(length(uv0) + iTime / 2.1), vec3(1.5)); // Adjust the power factor for contrast

        d = sin(d * 16.0 - cos(iTime)) / 8.0;
        d = abs(d);

        d = 0.05 / d;

        float threshold = 2.0; // Adjust the threshold for contrast
        vec3 blendedColor = mix(vec3(0.0), col, step(threshold, d));

        // Apply blending along the x-axis
        finalColor += blendedColor * xBlend;

        // Apply blending in the opposite direction along the x-axis
        finalColor += blendedColor * yBlend;

        // Reflect across the x-axis and y-axis
        if (mod(i, 2.0) == 0.0) {
            uv = vec2(uv.x, -uv.y);
        }
        if (i >= 2.0) {
            uv = vec2(-uv.x, uv.y);
        }
    }

    fragColor = vec4(finalColor, 2.0);
}

#define X_MIN 0.0
#define X_MAX 0.8
#define Y_MIN 0.42
#define Y_MAX 0.65

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    vec2 origUV = fragCoord.xy / iResolution.xy;

    uv *= 2.;
    uv -= vec2(2.5, 0.5);
    uv = clamp(uv, 0., 1.);

	float frame = floor( mod( iTime*10.0, 6.0 ) );

    //vec2 offset = vec2(0.2);
    //float scale = 3.;

	float nyanWidthUV = 40.0/256.0;
	uv.x = (uv.x + frame)*nyanWidthUV;


	vec4 nyan = texture( iChannel0, uv );
	vec3 col = nyan.xyz * nyan.a;

    float x = origUV.x - X_MIN; x /= X_MAX - X_MIN;
    if (x >= 0.0 && x <= 1.0 && nyan.a == 0.0)
    {
        x += iTime * 0.1;
        x = x * 5.0 - trunc(x * 5.0) - 0.5;
        if (x>0.0) origUV.y += 0.02;
        else origUV.y -= 0.02;
        float y = origUV.y - Y_MIN; y /= Y_MAX - Y_MIN;
        if (y >= 0.0 && y <= 1.0)
        {
            col.r = 1.0;
            col.g = sin((y+iTime*0.1)*20.0);
            col.b = sin((y-iTime*0.1)*20.0);
        }
    }

    fragColor = vec4( col, 1.0 );
}

#define OFFSET_X 0.02
#define OFFSET_Y 0.0
#define PULSE_SIZE 0.1
#define INV_PULSE_SIZE 10.0


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Hint, it's a few lines of code, and makes use of a tan function :)
    float pulseY = iTime*0.3 - trunc(iTime*0.3);
    bool insidePulse = false;
    if (uv.y > pulseY && uv.y - pulseY < PULSE_SIZE)
    {
        insidePulse = (uv.y > pulseY && uv.y - pulseY < PULSE_SIZE);
    }
    if (insidePulse)
    {
        float dy = (uv.y-pulseY) * INV_PULSE_SIZE - 0.5;
        if (sin(dy*200.0) > 0.0) uv.y +=dy*0.1;
    }

    vec2 offset1 = vec2(OFFSET_X, OFFSET_Y);
    vec2 offset2 = vec2(0.);
    vec2 offset3 = vec2(-OFFSET_X, OFFSET_Y);

    float r = texture(iChannel0, uv + offset1).r;
    float g = texture(iChannel0, uv + offset2).g;
    float b = texture(iChannel0, uv + offset3).b;

    vec3 col = vec3(r, g, b);

    // Output to screen
    fragColor = vec4(col,1.0);
}

