    fragColor = col;
}

// Type 1 Hexagonal Tilings, mla, 2023
//
// There are 3 types of convex monohedral hexagonal tilings, see:
// https://en.wikipedia.org/wiki/Hexagonal_tiling#Monohedral_convex_hexagonal_tilings
// or eg. Grunbaum and Shepard for details, and the same constructions
// works for non-convex tiles as well (though there may be non-convex tilings
// that are not constructible in this way).
//
// This shader constructs a Type 1 tiling based on a parallelogrammic
// repeating unit, and using two control points within the unit to
// construct the tiling (zooming in and using 'g' to see the underlying grid
// will probably be more useful than me trying to explain it). For
// some positions of the control points, the tile boundary intersects
// itself, or the tile is incompletely drawn (a limitation of doing this
// in a fragment shader).
//
// The resulting tiling consists of translations and 180Â° rotations of the
// basic tile, there are no reflections.
//
// If the edges of the tile are replaced with curves with appropriate symmetry,
// then we have tiling satisfying the 'Conway criterion':
// https://en.wikipedia.org/wiki/Conway_criterion
//
// By varying the control point positions across the image we can do
// 'parquet deformations' - currently just a displacement to the two
// control points. Better ways are possible I'm sure.
//
// Controls:
// <mouse>: if not in animation mode drag the control points.
// <up/down>: zoom in and out
// a: animation mode
// d: parquet deformation
// g: show underlying grid
// q,r,s: use special values for the control points
// x: don't show control points
// z: extra zoom out

// Point reflect p in q
vec2 pointreflect(vec2 p, vec2 q) {
  return 2.0*q - p;
}

int wind(vec2 p, vec2 q, vec2 r) {
  // NR winding number computation, extending ray
  // vertically.
  q -= p; r -= p; // Relative to p
  float t = determinant(mat2(q,r));
  if (q.x <= 0.0) {
    if (r.x > 0.0 && t > 0.0) return 1;
  } else {
    if (r.x <= 0.0 && t < 0.0) return -1;
  }
  return 0;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  setscale();
  vec2 p = map(fragCoord);
  vec2 p0 = p;
  float px = fwidth(length(p));
  vec2 a = getselection(0); // Parallelogram corner
  vec2 A = vec2(1,0);
  vec2 B = a;
  vec2 C = vec2(a.x-1.0,a.y);
  vec2 D = vec2(0);
  mat2 M = mat2(A,C);
  mat2 Minv = inverse(M);
  vec2 E = 0.5*(A+B);
  vec2 H = 0.5*(C+D);
  vec2 K = getselection(1);
  vec2 J = getselection(2);
  p = Minv*p;
  ivec2 ix = ivec2(floor(p));
  ivec2 inc = ivec2(1);
  p += 1.0;
  p = mod(p,2.0);
  p -= 1.0;
  if (p.x < 0.0) p = -p, inc = -inc;
  if (p.y < 0.0) p.y += 1.0;
  p = M*p;
  if (!key(CHAR_A)) {
    vec2 q = 2.0*fract(0.5+(0.2*iTime)*vec2(1,0.618));
    q = min(q,2.0-q);
    K = M*q;
    q = 2.0*fract(0.5+(0.1618*iTime)*vec2(0.618,1));
    q = min(q,2.0-q);
    J = M*q;
  }
  if (!key(CHAR_D)) {
    J += 0.025*p0;
    K += 0.025*p0.yx;
  }
  if (key(CHAR_Q)) K = C;
  if (key(CHAR_R)) J = B;
  if (key(CHAR_S)) J = K;
  float d = 1e8;
  d = min(d,segment(p,J,K));
  vec2 J1 = pointreflect(J,B);
  d = min(d,segment(p,J,J1));
  vec2 J0 = pointreflect(J,E);
  d = min(d,segment(p,J,J0));
  d = min(d,segment(p,J0,pointreflect(J0,A)));
  vec2 K0 = pointreflect(K,H);
  d = min(d,segment(p,K,K0));
  d = min(d,segment(p,K0,pointreflect(K0,D)));
  vec2 K1 = pointreflect(K,C);
  d = min(d,segment(p,K,K1));

  // Check for which region p is in. There are
  // some redundant calls to wind() here (since
  // wind(p,q,r) = -wind(p,r,q), more or less).
  int k;
  k = wind(p,K0,K)+wind(p,K,K1)+wind(p,K1,K0);
  if (k > 0) ix.x -= inc.x;
  k = wind(p,K1,K)+wind(p,K,J)+wind(p,J,J1)+wind(p,J1,K1);
  if (k > 0) ix.y += inc.y;
  k = wind(p,J1,J)+wind(p,J,J0)+wind(p,J0,J1);
  if (k > 0) ix.x += inc.x;
  vec3 col = vec3(1,1,0.8);
  if (!key(CHAR_C)) col = hsv2rgb(rand(ix+1),0.4,1.0);
  col *= smoothstep(0.0,px,d-0.01);
  if (key(CHAR_G)) {
    d = 1e8;
    d = min(d,line(p,A,B));
    d = min(d,line(p,B,C));
    d = min(d,line(p,C,D));
    d = min(d,line(p,D,A));
    col *= smoothstep(0.0,px,d);
  }
  if (!key(CHAR_X)) {
    // Show the control points
    float d = 1e8;
    d = min(d,distance(p0,a));
    d = min(d,distance(p0,K));
    d = min(d,distance(p0,J));
    col *= smoothstep(0.0,px,d-0.06);
  }
  col = pow(col,vec3(0.4545));
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1);
}

