vec3 box_min = vec3(-2.3,-1.3,-1.9);
vec3 box_max = vec3(2.1,2.6,2.2);
const float kPI = 3.1415926;

vec3 getSkyColor(vec3 dir){
    float t = (dir.y + 1.0) * 0.5;
    return (1.0 - t) * vec3(1.0, 1.0, 1.0) + t * vec3(0.5, 0.7, 1.0);
}

float getHeightPercent(vec3 pos)
{
    return (pos.y - box_min.y) / (box_max.y - box_min.y);
}

vec2 getUV(vec3 pos)
{
    return (pos.xz  / box_max.xz) * 0.5 + 0.5;
}

float remap(float originalValue, float originalMin, float originalMax, float newMin, float newMax)
{
	return newMin + (((originalValue - originalMin) / (originalMax - originalMin)) * (newMax - newMin));
}

float hgPhase(float g, float cosTheta)
{
    float numer = 1.0f - g * g;
    float denom = 1.0f + g * g + 2.0f * g * cosTheta;
    return numer / (4.0f * kPI * denom * sqrt(denom));
}

float dualLobPhase(float g0, float g1, float w, float cosTheta)
{
    return mix(hgPhase(g0, cosTheta), hgPhase(g1, cosTheta), w);
}

bool hitbox(vec3 ro,vec3 rd,inout vec3 start,inout vec3 end)
{
    vec3 t1 = (box_min - ro) / rd;
    vec3 t2 = (box_max - ro) / rd;

    vec3 tmin = min(t1,t2);
    vec3 tmax = max(t1,t2);

    float entt = max(tmin.x,max(tmin.y,tmin.z));
    float outt = min(tmax.x,min(tmax.y,tmax.z));

    if(entt > outt || outt < 0.) return false;

    start = ro + entt * rd;
    end   = ro + outt * rd;

    return true;
}

#define STRATUS_GRADIENT vec4(0.0, 0.1, 0.2, 0.3)
#define STRATOCUMULUS_GRADIENT vec4(0.02, 0.2, 0.48, 0.625)
#define CUMULUS_GRADIENT vec4(0.00, 0.1625, 0.88, 0.98)
float getDensityForCloud(float heightFraction, float cloudType)
{
	float stratusFactor = 1.0 - clamp(cloudType * 2.0, 0.0, 1.0);
	float stratoCumulusFactor = 1.0 - abs(cloudType - 0.5) * 2.0;
	float cumulusFactor = clamp(cloudType - 0.5, 0.0, 1.0) * 2.0;

	vec4 baseGradient = stratusFactor * STRATUS_GRADIENT + stratoCumulusFactor * STRATOCUMULUS_GRADIENT + cumulusFactor * CUMULUS_GRADIENT;
	return smoothstep(baseGradient.x, baseGradient.y, heightFraction) - smoothstep(baseGradient.z, baseGradient.w, heightFraction);
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float getDensity(vec3 pos)
{
    float height = getHeightPercent(pos);
    if(height < 0.0 || height > 1.0){
		return 0.0;
	}

    vec2 uv = getUV(pos);
    vec2 mv_uv = getUV(pos 
    + height * normalize(vec3(0.5, 0.5,0)) * 2.
    + normalize(vec3(0.5, 0.5,0)) * iTime * 0.5);

    vec4 density = texture(iChannel0,vec3(mv_uv / 2.,height));
    float fbm = dot(density.gba, vec3(0.625, 0.25,0.125));
    float base_cloud = remap(density.r, -(1.0 - fbm), 1., 0.0 , 1.0);

    float density2 = getDensityForCloud(height, 1.);
	base_cloud *= (density2 / height);

    vec3 erodeCloudNoise = texture(iChannel1, vec3(mv_uv, height)).rgb;
    float highFreqFBM = dot(erodeCloudNoise, vec3(0.625, 0.25, 0.125));
    float highFreqNoiseModifier = mix(highFreqFBM, 1.0 - highFreqFBM, height);
    base_cloud = base_cloud - highFreqNoiseModifier * (1.0 - base_cloud);

    return base_cloud - length(pos) ;

}

float LightRayMarch(vec3 start,float ds)
{
    vec3 dir = normalize(vec3(1,0.8,-1.));
    vec3 marchDir = ds * normalize(dir);
    float sigma_ds = -ds;

    float T = 1.0;
    vec3 pos = start;

    for(int i=0;i<6;i++)
    {
        float density = getDensity(pos);
        if(density > .0)
        {
            T *= exp(density * sigma_ds);
        }
    }

    return T;
}

vec4 RayMarch(vec3 start,vec3 end,vec3 bg,vec2 fragcoord)
{
    vec3 path = end - start;
    float len = length(path);
    float ds  =  len / 32.;
    vec3 marchDir = ds * normalize(path);

    float sigma_ds = -ds;

    vec3 scattering = vec3(0,0,0);
    float VoL = dot(normalize(path),normalize(vec3(1,0.8,-1.)));
    float T  = 1.0;

    vec3 pos = start;
    for(int i=0;i<32;i++)
    {
        float density = getDensity(pos);
        if(density > 0.)
        {
            float ds_Trans = exp(density * sigma_ds);

            float light_ds = LightRayMarch(pos,ds * 0.1);
            float sunPhase = dualLobPhase(0.5, -0.5, 0.2, -VoL);
            vec3 stepScattering = vec3(1,1.,0.9)*7. * ds_Trans * sunPhase * light_ds;
            vec3 sigmaS = vec3(density);
            scattering += stepScattering * T * (sigmaS * ds);

            T *= ds_Trans;
        }
        if(T < 1e-1) break;
        pos += marchDir;
    }

    vec3 finalColor = T * bg + scattering;

    return vec4(finalColor,T);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy) / iResolution.y;
    vec3 ro = vec3(0.0,0.0,5.0);
    vec3 rd = normalize(vec3(uv,-1.0));

    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 v = -1.0 + 2.0*q;
    v.x *= iResolution.x/ iResolution.y;

    #if 0
    vec2 mo = -1.0 + 2.0*iMouse.xy / iResolution.xy;
    #else
	vec2 mo = vec2(iTime*.1,cos(iTime*.25)*3.);
	#endif

    // camera by iq
    vec3 org = 5.0*normalize(vec3(cos(2.75-3.0*mo.x), 0.7-1.0*(mo.y-1.0), sin(2.75-3.0*mo.x)));
	vec3 ta = vec3(0.0, 1.0, 0.0);
    vec3 ww = normalize( ta - org);
    vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww ));
    vec3 vv = normalize(cross(ww,uu));
    vec3 dir = normalize( v.x*uu + v.y*vv + 1.5*ww );
	vec4 color=vec4(.0);

    vec3 start;
    vec3 end;
    vec3 bg  = getSkyColor(rd);
    vec4 col = vec4(bg,0);
    if(hitbox(ro,rd,start,end))
    {

        //float density = getDensity(start);
        //col.rgb = vec3(density,density,density);

        col = RayMarch(start,end,bg,fragCoord);
