/*
I created this algorithm to seamlessly tile a texture over a sphere 
with little distortions and no edges. It uses only 2 texture lookups.

This works by having 2 spheremappings. One is rotated so the edges of the 
spheremapping do not meet. I created a mask the blend between the edges
that has the same pattern as a tennisball. This mapping includes normals 
too. The blend region is currently hidden by the glue-line, but you can 
make it visible by enabling SHOWBLEND. 

There are several global constants below to enable debug views of the
mapping and mouse controls to look around.

See also my demo for tangent space matrix of spheremaps:
https://www.shadertoy.com/view/ctXfDS
*/

// enables debug grid where the two mappings blend with each other
// Green and Red are the upper and lower bounds of the weights
// Blue and White is the edge where one tiling breaks
//#define SHOWBLEND
#define LINETHICKNESS 0.02

// 1 no contrast, higher values increase contrast
#define CONTRAST 16.0

// enable to show the alpha/weights otherwise shows textures
//#define SHOWALPHA

// Signed distance function of a tennis ball, the glueline would be at 0.5
#define TWO_OVER_PI 0.6366197724 // 2. / PI
float tennisballSDF (vec3 normal) {   
    // Prevent floating point errors
    normal = clamp(normal, -1., 1.);

    // combine halfs of SDF
    float tennisballSDF;
    if (normal.x > 0.) {
        // poles
        tennisballSDF = 1.- acos(abs(normal.z)) * TWO_OVER_PI; 
    } else {
        // seam of the edge between poles
        tennisballSDF = acos(abs(normal.y)) * TWO_OVER_PI; 
    }
    return tennisballSDF;    
}

//Spherical mapping
vec2 sphereMap(vec3 normal) {
    // merkator projection
    float u = 0.5 + atan(normal.y, normal.x) / (2.*3.1415);
    float v = 0.5 + asin(normal.z) / 3.1415;
    return vec2(u, v);
}

// TBN Matrix for the spheremapping above
mat3 sphereTBNMatrix(vec3 normal) {
    // vec3 poleAxis = vec3(0,0,1.);
    // vec3 tangent = normalize(cross(poleAxis, normal));
    vec3 tangent = safeNormalize(vec3(-normal.y, normal.x, 0.), vec3(1,0,0));
    vec3 bitangent = cross(normal, tangent);
    return mat3(tangent, bitangent, normal);
}

// simple spheremap texture sampler
materialVal sphereMapSample(sampler2D tex, vec3 normal, float scale) {
    // sample spheremap
    vec2 uv = sphereMap(normal);

    // so UVs are ~equal scale, as X is circumfrence, but Y is only half
    uv.x *= 2.; 

    // apply scale
    uv *= scale;    

    // sample tbn matrix
    mat3 tbn = sphereTBNMatrix(normal);  

    // sample texture, since we hide the seam we can ignore mips
    vec3 color = texture(tex, uv).xyz;

    // sample normal, if you can obviously use a normal texture instead
    vec3 tsNormal = HeightToNormal(tex, uv, iChannelResolution[0].xy, 4.0);
    // translate normals to world space
    vec3 wsNormal = tbn * tsNormal;

    // pack everything into a struct
    return materialVal(color, wsNormal, 0.);
}

materialVal tennisballmap(sampler2D tex, vec3 normal, float scale, float contrast) {    
    // rotated spheremap to align with tennisball other side
    vec3 rotatedNormal = vec3(-normal.x, normal.z, normal.y); 

    // sample textures
    materialVal sphereA = sphereMapSample(tex, normal, scale);
    materialVal sphereB = sphereMapSample(tex, rotatedNormal, scale);

    // sample blend weight
    float alpha = tennisballSDF(normal);

    // increase contrast of tennisball mask
    float contrastAlpha = straightContrast(alpha, contrast);

    #ifdef SHOWALPHA
        // show alpha/weights with best colormap for better readability
        vec3 col = viridis(contrastAlpha);
    #else    
        // interpolate between mappings to remove edges and poles
        vec3 col = mix(sphereA.color, sphereB.color, contrastAlpha);
    #endif

    // since sphere B is rotated its normals need to be rotated as well
    vec3 wsNormalB = vec3(-sphereB.normal.x, sphereB.normal.z, sphereB.normal.y);

    // interpolate normals, Slerp may be better, but I can't tell the differene
    vec3 wsNormal = normalize(mix(sphereA.normal, wsNormalB, contrastAlpha));

    #ifdef SHOWBLEND
        // overlaying the edges of the alpha mask
        vec2 blend = step(abs(vec2(contrastAlpha, 1.-contrastAlpha)-0.1), vec2(LINETHICKNESS));

        // edge of the mappings
        float edgeA = step(alpha, LINETHICKNESS);;
        float edgeB = step(tennisballSDF(rotatedNormal), LINETHICKNESS);

        // join color
        col = max(max(col, vec3(blend, edgeA)), vec3(edgeB));
    #endif

    // pack everything into a struct
    return materialVal(col, wsNormal, alpha);
}

// samples tennisball textures and applies tennis ball colors
materialVal tennisball(sampler2D tex, vec3 normal, float scale, float contrast) {
    // sample textures
    materialVal ball = tennisballmap(tex, normal, scale, contrast);

    // center the mask on the line at 0.5
    float line = abs(ball.mask-0.5)*2.;

    // calculate a little dark line around the glue edge
    float glueWidth = 0.08;
    float tintWidth = 0.04;
    float tint = smoothstep(glueWidth + tintWidth, glueWidth - tintWidth, line);

    // define ball colors
    vec3 glueColor = vec3(0.824,0.827,0.749) * tint;
    vec3 feltColor = vec3(0.937,0.953,0.086) - (ball.color * 0.5);

    // caluclate smoothstep for weight between glue and felt
    float stepWidth = 0.02;
    float alpha = smoothstep(glueWidth - stepWidth,glueWidth + stepWidth, line);

    // blend colors
    vec3 color = mix(glueColor, feltColor, alpha);

    // interpolate normals, Slerp may be better, but I can't tell the differene
    vec3 wsNormal = normalize(mix(normal, ball.normal, alpha));

    return materialVal(color, wsNormal, ball.mask);
}

// _____________________________3D Section_____________________________
// https://www.shadertoy.com/view/4sjXW1 by nimitz (twitter: @stormoid)

mat2 mm2(in float a){float c = cos(a), s = sin(a);return mat2(c,-s,s,c);}

vec2 iSphere(in vec3 ro, in vec3 rd) {
    vec3 oc = ro;
    float b = dot(oc, rd);
    float c = dot(oc,oc) - 1.;
    float h = b*b - c;
    //if(h <0.0) return vec2(-1.);
    return vec2(-b - sqrt(h), h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {	
	vec2 p = fragCoord.xy/iResolution.xy-0.5;
	p.x*=iResolution.x/iResolution.y;
	vec2 um = iMouse.xy / iResolution.xy-.5;
	um.x *= iResolution.x/iResolution.y;
    p*= 1.5;

    //camera
	vec3 ro = vec3(0.,0.,2.4);
    vec3 rd = normalize(vec3(p,-1.5));
    mat2 mx;
    mat2 my;
    vec2 cursor;
    if(iMouse.x > 0.0)	{    
        mx = mm2(um.x*5.);
        my = mm2(um.y*5.);

                // scale mouse coords to 0-1 range of screen
        vec2 mouse = iMouse.xy / iResolution.y;
        vec2 coords = fragCoord / iResolution.y;

        // Generate Mouse coursor, x = mouse color, y = mouse weight 
        float mouseSize = 0.02;
        float mouseDist = length(coords - mouse); 
        float aa = fwidth(mouseDist); // cheap anti-aliasing factor
        cursor.x = smoothstep(mouseSize*0.5 + aa, mouseSize*0.5 - aa, mouseDist);
        cursor.y = smoothstep(mouseSize + aa, mouseSize - aa, mouseDist);

    } else {
        mx = mm2(-iTime*.5+um.x*5.);
        my = mm2(iTime*0.2+um.y*5.);
    }
    ro.xz *= mx;rd.xz *= mx;
    ro.xy *= my;rd.xy *= my;

    vec2 t = iSphere(ro,rd);

    vec3 lightDir = normalize(vec3(1,0,1));

    // alternate between backgroudn and sphere mapping
    vec3 colorA;
    if (t.x > 0.) {
        // Sphere mapping
    	vec3 normal = ro+rd*t.x;

        #if defined(SHOWBLEND) || defined(SHOWALPHA)
            // sample albedo textures
            materialVal cube = tennisballmap(iChannel0, normal, 5., CONTRAST);

            colorA = cube.color;
        #else
            // sample albedo textures
            materialVal cube = tennisball(iChannel0, normal, 5., CONTRAST);

            // Apply lighting to show the normals too
            colorA = phongLighting(cube.color, cube.normal, ro, lightDir);
        #endif

    } 

    // Background mapping
    vec3 normal = rd;

    // make tennisball sdf
    float alpha = tennisballSDF(normal);

    // increase contrast a bit
    alpha = straightContrast(1.-alpha, 1.5);

    // apply best colormap
    vec3 albedo = viridis(alpha);

    #if defined(SHOWBLEND) || defined(SHOWALPHA)
        // make color a bit darker for better contrast with sphere
        vec3 colorB = pow(albedo, vec3(1.5));

    #else
        // simple sun with halo
        vec3 sun = sun(normal, lightDir);

        // combine albedo and sunlight
        vec3 colorB  = albedo + sun;
    #endif

    // Smooth blending of ball with background
    vec3 color = mix(colorA, colorB, smoothstep(0.02, 0.0, t.y));

    // Add mouse cursor
    color = mix(color, vec3(cursor.x), cursor.y);

	fragColor = vec4(color, 1.0);
}

